import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/card';
import { Button } from '../ui/button';
import { Input } from '../ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../ui/select';
import { Calculator, Info, RotateCcw, AlertTriangle, Activity } from 'lucide-react';
import { Badge } from '../ui/badge';

const BMICalculator = ({ peptide, onCalculate, compact = false }) => {
  const [formData, setFormData] = useState({
    weight: '',
    heightFeet: '',
    heightInches: '',
    heightCm: '',
    unit: 'metric' // 'metric' or 'imperial'
  });
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Load saved inputs
  useEffect(() => {
    const savedData = localStorage.getItem('bmi-calculator-data');
    if (savedData) {
      try {
        const parsed = JSON.parse(savedData);
        setFormData(prev => ({ ...prev, ...parsed }));
      } catch (e) {
        console.error('Error loading saved BMI calculator data:', e);
      }
    }
  }, []);

  // Save inputs whenever form data changes
  useEffect(() => {
    if (Object.values(formData).some(val => val !== '')) {
      localStorage.setItem('bmi-calculator-data', JSON.stringify(formData));
    }
  }, [formData]);

  const updateFormData = (key, value) => {
    setFormData(prev => ({ ...prev, [key]: value }));
  };

  const calculateBMI = () => {
    if (!validateInputs()) return;

    setLoading(true);
    setError(null);

    try {
      const calculatedResult = performCalculation();
      setResult(calculatedResult);

      // Emit analytics event
      if (onCalculate) {
        onCalculate({
          type: 'bmi',
          peptide: peptide?.slug || 'standalone',
          inputs: formData,
          result: calculatedResult
        });
      }

    } catch (err) {
      setError('BMI calculation failed. Please try again.');
      console.error('BMI calculator error:', err);
    } finally {
      setLoading(false);
    }
  };

  const validateInputs = () => {
    if (formData.unit === 'metric') {
      if (!formData.weight || !formData.heightCm || formData.weight <= 0 || formData.heightCm <= 0) {
        setError('Please enter valid weight (kg) and height (cm)');
        return false;
      }
    } else {
      if (!formData.weight || !formData.heightFeet || formData.weight <= 0 || formData.heightFeet < 0) {
        setError('Please enter valid weight (lb) and height (feet/inches)');
        return false;
      }
    }
    return true;
  };

  const performCalculation = () => {
    let bmi, weightKg, heightM, totalInches;

    if (formData.unit === 'metric') {
      // Metric calculation
      weightKg = parseFloat(formData.weight);
      heightM = parseFloat(formData.heightCm) / 100; // Convert cm to meters
      bmi = weightKg / (heightM * heightM);
    } else {
      // Imperial calculation
      const weightLb = parseFloat(formData.weight);
      const feet = parseInt(formData.heightFeet) || 0;
      const inches = parseInt(formData.heightInches) || 0;
      totalInches = feet * 12 + inches;

      // Convert to metric for healthy range calculation
      heightM = totalInches * 0.0254;
      weightKg = weightLb / 2.20462;

      // Calculate BMI using imperial formula
      bmi = (weightLb * 703) / (totalInches * totalInches);
    }

    // Determine BMI category
    let category, categoryColor;
    if (bmi < 18.5) {
      category = 'Underweight';
      categoryColor = 'text-blue-600';
    } else if (bmi < 25.0) {
      category = 'Normal weight';
      categoryColor = 'text-green-600';
    } else if (bmi < 30.0) {
      category = 'Overweight';
      categoryColor = 'text-orange-600';
    } else if (bmi < 35.0) {
      category = 'Obesity Class I (Moderate)';
      categoryColor = 'text-red-600';
    } else if (bmi < 40.0) {
      category = 'Obesity Class II (Severe)';
      categoryColor = 'text-red-700';
    } else {
      category = 'Obesity Class III (Very Severe)';
      categoryColor = 'text-red-800';
    }

    // Calculate healthy weight range
    const minWeightKg = 18.5 * (heightM * heightM);
    const maxWeightKg = 24.9 * (heightM * heightM);

    let minWeight, maxWeight, currentWeight, weightDelta = 0, isOverweight = false;

    if (formData.unit === 'metric') {
      minWeight = minWeightKg.toFixed(1);
      maxWeight = maxWeightKg.toFixed(1);
      currentWeight = weightKg.toFixed(1);

      if (weightKg > maxWeightKg) {
        weightDelta = (weightKg - maxWeightKg).toFixed(1);
        isOverweight = true;
      } else if (weightKg < minWeightKg) {
        weightDelta = (minWeightKg - weightKg).toFixed(1);
        isOverweight = false;
      }
    } else {
      const minWeightLb = minWeightKg * 2.20462;
      const maxWeightLb = maxWeightKg * 2.20462;
      const currentWeightLb = parseFloat(formData.weight);

      minWeight = minWeightLb.toFixed(0);
      maxWeight = maxWeightLb.toFixed(0);
      currentWeight = currentWeightLb.toFixed(0);

      if (currentWeightLb > maxWeightLb) {
        weightDelta = (currentWeightLb - maxWeightLb).toFixed(0);
        isOverweight = true;
      } else if (currentWeightLb < minWeightLb) {
        weightDelta = (minWeightLb - currentWeightLb).toFixed(0);
        isOverweight = false;
      }
    }

    return {
      bmi: bmi.toFixed(1),
      category,
      categoryColor,
      minWeight,
      maxWeight,
      currentWeight,
      weightDelta,
      isOverweight,
      unit: formData.unit,
      weightUnit: formData.unit === 'metric' ? 'kg' : 'lb',
      heightDisplay: formData.unit === 'metric' 
        ? `${formData.heightCm} cm` 
        : `${formData.heightFeet}'${formData.heightInches || 0}"`
    };
  };

  const resetCalculator = () => {
    setFormData({
      weight: '',
      heightFeet: '',
      heightInches: '',
      heightCm: '',
      unit: 'metric'
    });
    setResult(null);
    setError(null);
    localStorage.removeItem('bmi-calculator-data');
  };

  if (compact && error) {
    return (
      <Card className="border-orange-200 bg-orange-50">
        <CardContent className="p-4">
          <div className="flex items-center text-orange-700">
            <AlertTriangle className="h-4 w-4 mr-2" />
            <span className="text-sm">BMI Calculator temporarily unavailable</span>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className={`border-blue-200 bg-blue-50 ${compact ? 'mt-4' : ''}`}>
      <CardHeader className="pb-3">
        <CardTitle className={`font-medium flex items-center text-blue-900 ${compact ? 'text-sm' : 'text-base'}`}>
          <Activity className={`mr-2 ${compact ? 'h-4 w-4' : 'h-5 w-5'}`} />
          {peptide ? `BMI Calculator - ${peptide.name}` : 'BMI Calculator'}
        </CardTitle>
        {peptide && (
          <div className="text-xs text-blue-700">
            Calculate your BMI to optimize {peptide.name} dosing protocols
          </div>
        )}
      </CardHeader>

      <CardContent className="pt-0 space-y-3">
        {/* Unit Selection */}
        <div>
          <label className={`text-gray-600 mb-1 block ${compact ? 'text-xs' : 'text-sm'}`}>
            Unit System
          </label>
          <Select value={formData.unit} onValueChange={(value) => updateFormData('unit', value)}>
            <SelectTrigger className={compact ? 'h-8 text-sm' : ''}>
              <SelectValue placeholder="Select units" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="metric">Metric (kg, cm)</SelectItem>
              <SelectItem value="imperial">Imperial (lb, ft/in)</SelectItem>
            </SelectContent>
          </Select>
        </div>

        {/* Input Fields */}
        <div className="grid grid-cols-1 gap-2">
          {/* Weight */}
          <div>
            <label className={`text-gray-600 mb-1 block ${compact ? 'text-xs' : 'text-sm'}`}>
              Weight ({formData.unit === 'metric' ? 'kg' : 'lbs'})
            </label>
            <Input
              type="number"
              placeholder={formData.unit === 'metric' ? '70' : '154'}
              value={formData.weight}
              onChange={(e) => updateFormData('weight', e.target.value)}
              className={compact ? 'h-8 text-sm' : ''}
            />
          </div>

          {/* Height */}
          {formData.unit === 'metric' ? (
            <div>
              <label className={`text-gray-600 mb-1 block ${compact ? 'text-xs' : 'text-sm'}`}>
                Height (cm)
              </label>
              <Input
                type="number"
                placeholder="175"
                value={formData.heightCm}
                onChange={(e) => updateFormData('heightCm', e.target.value)}
                className={compact ? 'h-8 text-sm' : ''}
              />
            </div>
          ) : (
            <div className="grid grid-cols-2 gap-2">
              <div>
                <label className={`text-gray-600 mb-1 block ${compact ? 'text-xs' : 'text-sm'}`}>
                  Feet
                </label>
                <Input
                  type="number"
                  placeholder="5"
                  value={formData.heightFeet}
                  onChange={(e) => updateFormData('heightFeet', e.target.value)}
                  className={compact ? 'h-8 text-sm' : ''}
                />
              </div>
              <div>
                <label className={`text-gray-600 mb-1 block ${compact ? 'text-xs' : 'text-sm'}`}>
                  Inches
                </label>
                <Input
                  type="number"
                  placeholder="9"
                  min="0"
                  max="11"
                  value={formData.heightInches}
                  onChange={(e) => updateFormData('heightInches', e.target.value)}
                  className={compact ? 'h-8 text-sm' : ''}
                />
              </div>
            </div>
          )}
        </div>

        {/* Action Buttons */}
        <div className="flex gap-2">
          <Button 
            onClick={calculateBMI} 
            disabled={loading}
            size={compact ? "sm" : "default"}
            className={`flex-1 ${compact ? 'h-8 text-xs' : ''}`}
          >
            {loading ? 'Calculating...' : 'Calculate BMI'}
          </Button>
          <Button 
            variant="outline" 
            size={compact ? "sm" : "default"}
            onClick={resetCalculator}
            className={compact ? 'h-8 px-2' : ''}
          >
            <RotateCcw className={compact ? 'h-3 w-3' : 'h-4 w-4'} />
          </Button>
        </div>

        {/* Error Display */}
        {error && (
          <div className="text-red-600 text-xs bg-red-50 p-2 rounded border border-red-200">
            {error}
          </div>
        )}

        {/* Results Display */}
        {result && (
          <div className="bg-white border rounded p-3 space-y-3">
            <div className={`font-medium text-gray-900 mb-2 ${compact ? 'text-xs' : 'text-sm'}`}>BMI Results:</div>

            {/* BMI Value and Category */}
            <div className="text-center">
              <div className={`font-bold text-gray-900 ${compact ? 'text-lg' : 'text-2xl'}`}>
                BMI: {result.bmi}
              </div>
              <div className={`font-medium ${result.categoryColor} ${compact ? 'text-sm' : 'text-base'}`}>
                {result.category}
              </div>
            </div>

            {/* Healthy Weight Range */}
            <div className={`bg-gray-50 p-2 rounded ${compact ? 'text-xs' : 'text-sm'}`}>
              <div className="font-medium text-gray-700 mb-1">Healthy Weight Range:</div>
              <div className="text-gray-600">
                {result.minWeight} - {result.maxWeight} {result.weightUnit}
              </div>
              <div className="text-gray-600">
                Current: {result.currentWeight} {result.weightUnit}
              </div>

              {result.weightDelta > 0 && (
                <div className={`mt-1 font-medium ${result.isOverweight ? 'text-orange-600' : 'text-blue-600'}`}>
                  {result.isOverweight 
                    ? `${result.weightDelta} ${result.weightUnit} above healthy range`
                    : `${result.weightDelta} ${result.weightUnit} below healthy range`
                  }
                </div>
              )}
            </div>

            {/* WHO Categories Reference */}
            {!compact && (
              <div className="text-xs text-gray-500 border-t pt-2">
                <div className="font-medium mb-1">WHO BMI Categories:</div>
                <div>Underweight: &lt; 18.5</div>
                <div>Normal: 18.5 - 24.9</div>
                <div>Overweight: 25.0 - 29.9</div>
                <div>Obesity: â‰¥ 30.0</div>
              </div>
            )}

            <div className="flex items-center justify-between pt-2 border-t">
              <Badge variant="outline" className="text-xs">
                <Info className="h-3 w-3 mr-1" />
                Health Reference Only
              </Badge>
              {peptide && (
                <div className="text-xs text-gray-500">
                  For {peptide.name} protocols
                </div>
              )}
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
};

export default BMICalculator;

