import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/card';
import { Button } from '../ui/button';
import { Input } from '../ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../ui/select';
import { Calculator, Info, Save, RotateCcw, AlertTriangle, Activity } from 'lucide-react';
import { Badge } from '../ui/badge';
import BMICalculator from './BMICalculator';

const MiniCalculator = ({ peptide, onCalculate }) => {
  const [formData, setFormData] = useState({});
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('dosage'); // 'dosage' or 'bmi'

  // Check if this is a GLP-1 peptide that should have BMI calculator
  const isGLP1Peptide = peptide && (
    peptide.slug === 'semaglutide' || 
    peptide.slug === 'tirzepatide' ||
    peptide.slug === 'liraglutide' ||
    peptide.slug === 'dulaglutide' ||
    peptide.slug === 'exenatide' ||
    peptide.slug === 'retatrutide' ||
    peptide.slug === 'survodutide' ||
    peptide.slug === 'mazdutide'
  );

  // GLP-1 BMI Calculator Logic (same as GLP1CalculatorPage)
  const WEEKLY_RATE = {
    semaglutide: 0.0022,
    tirzepatide: 0.0029,
    retatrutide: 0.0050,
    liraglutide: 0.0014,
    mazdutide: 0.0026,
    survodutide: 0.0032,
    dulaglutide: 0.0007
  };

  // BMI calculation functions
  const toKg = lb => lb / 2.20462;
  const toLb = kg => kg * 2.20462;
  const inchesFromFI = (feet, inches) => feet * 12 + inches;
  const metersFromInches = inches => inches * 0.0254;
  const metersFromCm = cm => cm / 100;
  const bmiMetric = (kg, m) => kg / (m * m);
  const bmiImperial = (lb, inches) => (lb * 703) / (inches * inches);

  function parseFeetInches(input) {
    const s = String(input).trim();
    if (!s.includes('.')) return { feet: Number(s), inches: 0 };
    const [feetStr, inchStrRaw] = s.split('.');
    const inchStr = inchStrRaw.slice(0, 2);
    let feet = Number(feetStr);
    let inches = Number(inchStr);
    if (!Number.isFinite(feet) || !Number.isFinite(inches)) throw new Error('Invalid height');
    if (inches >= 12) { feet += Math.floor(inches / 12); inches = inches % 12; }
    return { feet, inches };
  }

  function healthyRangeKg(m) {
    const m2 = m * m;
    return { min: 18.5 * m2, optimal: 22.0 * m2, max: 24.9 * m2 };
  }

  function getBMICategory(bmi) {
    if (bmi < 16.0) return 'Severely Underweight';
    if (bmi < 18.5) return 'Underweight';
    if (bmi < 25.0) return 'Normal Weight';
    if (bmi < 30.0) return 'Overweight';
    if (bmi < 35.0) return 'Moderately Obese Class I';
    if (bmi < 40.0) return 'Severely Obese Class II';
    return 'Very Severely Obese Class III';
  }

  function weeklyDeficitLoss(unit, deficitPerDayKcal) {
    if (deficitPerDayKcal <= 0) return 0;
    return unit === 'imperial' ? (deficitPerDayKcal * 7) / 3500 : (deficitPerDayKcal * 7) / 7700;
  }

  function weeksToGoal(W0, Wg, r, d) {
    if (W0 <= Wg) return 0;
    if (r <= 0) return d > 0 ? Math.ceil((W0 - Wg) / d) : Infinity;
    const ratio = (Wg + d / r) / (W0 + d / r);
    if (ratio <= 0) return Infinity;
    const n = Math.log(ratio) / Math.log(1 - r);
    return Math.max(0, Math.ceil(n));
  }

  // Load saved inputs for this peptide
  useEffect(() => {
    const savedData = localStorage.getItem(`calculator-${peptide.slug}`);
    if (savedData) {
      try {
        setFormData(JSON.parse(savedData));
      } catch (e) {
        console.error('Error loading saved calculator data:', e);
      }
    }
  }, [peptide.slug]);

  // Save inputs whenever form data changes
  useEffect(() => {
    if (Object.keys(formData).length > 0) {
      localStorage.setItem(`calculator-${peptide.slug}`, JSON.stringify(formData));
    }
  }, [formData, peptide.slug]);

  const updateFormData = (key, value) => {
    setFormData(prev => ({ ...prev, [key]: value }));
  };

  const calculateDosage = async () => {
    if (!validateInputs()) return;

    setLoading(true);
    setError(null);

    try {
      // Simulate API call with timeout for fallback testing
      await new Promise(resolve => setTimeout(resolve, 1000));

      const calculatedResult = performCalculation();
      setResult(calculatedResult);

      // Emit analytics event
      if (onCalculate) {
        onCalculate({
          peptide: peptide.slug,
          inputs: formData,
          result: calculatedResult
        });
      }

    } catch (err) {
      setError('Calculation failed. Please try again.');
      console.error('Calculator error:', err);
    } finally {
      setLoading(false);
    }
  };

  const validateInputs = () => {
    const config = getCalculatorConfig();
    for (const field of config.requiredFields) {
      if (!formData[field.key] || formData[field.key] <= 0) {
        setError(`Please enter a valid ${field.label.toLowerCase()}`);
        return false;
      }
    }
    return true;
  };

  const performCalculation = () => {
    const config = getCalculatorConfig();
    return config.calculate(formData);
  };

  const getCalculatorConfig = () => {
    // Universal calculator configuration based on peptide type and properties
    const getUniversalConfig = (peptide) => {
      // Determine if peptide uses mcg or mg dosing based on common patterns
      const isMcgDosing = peptide.dosage && (
        peptide.dosage.includes('mcg') || 
        peptide.dosage.includes('Î¼g') ||
        peptide.slug.includes('ghrp') ||
        peptide.slug.includes('igf') ||
        peptide.slug.includes('hexarelin') ||
        peptide.slug.includes('ipamorelin') ||
        peptide.slug.includes('melanotan') ||
        peptide.slug.includes('bpc') ||
        peptide.slug.includes('tb-500') ||
        peptide.slug.includes('ghk')
      );

      // Extract suggested doses from peptide data
      let defaultVialSize = '5';
      let defaultTargetDose = isMcgDosing ? '200' : '1';
      let doseUnit = isMcgDosing ? 'mcg' : 'mg';

      // Parse peptide-specific defaults from dosage field
      if (peptide.dosage) {
        const dosageText = peptide.dosage.toLowerCase();

        // Extract common vial sizes
        if (dosageText.includes('10mg') || dosageText.includes('10 mg')) defaultVialSize = '10';
        else if (dosageText.includes('5mg') || dosageText.includes('5 mg')) defaultVialSize = '5';
        else if (dosageText.includes('2mg') || dosageText.includes('2 mg')) defaultVialSize = '2';
        else if (dosageText.includes('1mg') || dosageText.includes('1 mg')) defaultVialSize = '1';

        // Extract target doses
        const mcgMatches = dosageText.match(/(\d+)-?(\d+)?\s*mcg/);
        const mgMatches = dosageText.match(/(\d+\.?\d*)-?(\d+\.?\d*)?\s*mg/);

        if (isMcgDosing && mcgMatches) {
          defaultTargetDose = mcgMatches[1];
        } else if (!isMcgDosing && mgMatches) {
          defaultTargetDose = mgMatches[1];
        }

        // Special cases for specific peptides
        if (peptide.slug === 'semaglutide') defaultTargetDose = '0.25';
        else if (peptide.slug === 'tirzepatide') defaultTargetDose = '2.5';
        else if (peptide.slug === 'bpc-157') defaultTargetDose = '250';
        else if (peptide.slug === 'tb-500') defaultTargetDose = '2';
        else if (peptide.slug === 'ipamorelin') defaultTargetDose = '200';
        else if (peptide.slug === 'igf-1-lr3') defaultTargetDose = '40';
      }

      return {
        title: `${peptide.name} Dosage Calculator`,
        requiredFields: [
          { key: 'vialSize', label: 'Vial Size (mg)', type: 'number', placeholder: defaultVialSize },
          { key: 'bacteriostaticWater', label: 'Bacteriostatic Water (ml)', type: 'number', placeholder: '2' },
          { key: 'targetDose', label: `Target Dose (${doseUnit})`, type: 'number', placeholder: defaultTargetDose }
        ],
        calculate: (data) => {
          if (isMcgDosing) {
            // For mcg dosing (peptides like growth factors, BPC-157, etc.)
            const concentrationMg = data.vialSize / data.bacteriostaticWater;
            const concentrationMcg = concentrationMg * 1000;
            const injectionVolume = data.targetDose / concentrationMcg;

            return {
              concentration: concentrationMg.toFixed(2),
              concentrationMcg: concentrationMcg.toFixed(0),
              injectionVolume: injectionVolume.toFixed(3),
              dosesPerVial: Math.floor((data.vialSize * 1000) / data.targetDose),
              doseUnit: 'mcg'
            };
          } else {
            // For mg dosing (GLP-1 agonists, etc.)
            const concentration = data.vialSize / data.bacteriostaticWater;
            const injectionVolume = data.targetDose / concentration;
            const unitsPerML = concentration * 1000;

            return {
              concentration: concentration.toFixed(2),
              injectionVolume: injectionVolume.toFixed(3),
              unitsPerML: unitsPerML.toFixed(0),
              dosesPerVial: Math.floor(data.vialSize / data.targetDose),
              doseUnit: 'mg'
            };
          }
        }
      };
    };

    // Predefined configs for specific peptides (override universal)
    const specificConfigs = {
      'semaglutide': {
        title: 'Semaglutide Dosage Calculator',
        requiredFields: [
          { key: 'vialSize', label: 'Vial Size (mg)', type: 'number', placeholder: '5' },
          { key: 'bacteriostaticWater', label: 'Bacteriostatic Water (ml)', type: 'number', placeholder: '2' },
          { key: 'targetDose', label: 'Target Dose (mg)', type: 'number', placeholder: '0.25' }
        ],
        calculate: (data) => {
          const concentration = data.vialSize / data.bacteriostaticWater;
          const injectionVolume = data.targetDose / concentration;
          const unitsPerML = concentration * 1000;

          return {
            concentration: concentration.toFixed(2),
            injectionVolume: injectionVolume.toFixed(3),
            unitsPerML: unitsPerML.toFixed(0),
            dosesPerVial: Math.floor(data.vialSize / data.targetDose),
            doseUnit: 'mg'
          };
        }
      },
      'tirzepatide': {
        title: 'Tirzepatide Dosage Calculator',
        requiredFields: [
          { key: 'vialSize', label: 'Vial Size (mg)', type: 'number', placeholder: '10' },
          { key: 'bacteriostaticWater', label: 'Bacteriostatic Water (ml)', type: 'number', placeholder: '2' },
          { key: 'targetDose', label: 'Target Dose (mg)', type: 'number', placeholder: '2.5' }
        ],
        calculate: (data) => {
          const concentration = data.vialSize / data.bacteriostaticWater;
          const injectionVolume = data.targetDose / concentration;
          const unitsPerML = concentration * 1000;

          return {
            concentration: concentration.toFixed(2),
            injectionVolume: injectionVolume.toFixed(3),
            unitsPerML: unitsPerML.toFixed(0),
            dosesPerVial: Math.floor(data.vialSize / data.targetDose),
            doseUnit: 'mg'
          };
        }
      }
    };

    // Return specific config if available, otherwise use universal
    return specificConfigs[peptide.slug] || getUniversalConfig(peptide);
  };

  const resetCalculator = () => {
    setFormData({});
    setResult(null);
    setError(null);
    localStorage.removeItem(`calculator-${peptide.slug}`);
  };

  const config = getCalculatorConfig();

  // Fallback component for failed loading
  if (error && !result) {
    return (
      <Card className="border-orange-200 bg-orange-50">
        <CardContent className="p-4">
          <div className="flex items-center text-orange-700">
            <AlertTriangle className="h-4 w-4 mr-2" />
            <span className="text-sm">Calculator temporarily unavailable</span>
          </div>
          <Button 
            variant="outline" 
            size="sm" 
            onClick={() => setError(null)}
            className="mt-2"
          >
            Try Again
          </Button>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="border-blue-200 bg-blue-50">
      <CardHeader className="pb-3">
        {/* Tab Navigation for GLP-1 peptides */}
        {isGLP1Peptide && (
          <div className="flex mb-3 bg-white rounded-lg p-1 border">
            <button
              onClick={() => setActiveTab('dosage')}
              className={`flex-1 flex items-center justify-center px-3 py-2 rounded-md text-sm font-medium transition-colors ${
                activeTab === 'dosage'
                  ? 'bg-blue-100 text-blue-700 border border-blue-300'
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              <Calculator className="h-4 w-4 mr-1" />
              Dosage
            </button>
            <button
              onClick={() => setActiveTab('bmi')}
              className={`flex-1 flex items-center justify-center px-3 py-2 rounded-md text-sm font-medium transition-colors ${
                activeTab === 'bmi'
                  ? 'bg-blue-100 text-blue-700 border border-blue-300'
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              <Activity className="h-4 w-4 mr-1" />
              BMI
            </button>
          </div>
        )}

        <CardTitle className="text-sm font-medium flex items-center text-blue-900">
          {activeTab === 'bmi' && isGLP1Peptide ? (
            <>
              <Activity className="h-4 w-4 mr-2" />
              BMI Calculator - {peptide.name}
            </>
          ) : (
            <>
              <Calculator className="h-4 w-4 mr-2" />
              {config.title}
            </>
          )}
        </CardTitle>
        {isGLP1Peptide && activeTab === 'bmi' && (
          <div className="text-xs text-blue-700">
            Calculate BMI to optimize {peptide.name} weight management protocols
          </div>
        )}
      </CardHeader>

      {/* Tab Content */}
      {activeTab === 'bmi' && isGLP1Peptide ? (
        <CardContent className="pt-0">
          <BMICalculator 
            peptide={peptide} 
            onCalculate={onCalculate}
            compact={true}
          />
        </CardContent>
      ) : (
        /* Original Dosage Calculator Content */
        <CardContent className="pt-0 space-y-3">
          {/* Input Fields */}
          {config.requiredFields.map((field) => (
            <div key={field.key}>
              <label className="text-xs text-gray-600 mb-1 block">
                {field.label}
              </label>
              <Input
                type={field.type}
                placeholder={field.placeholder}
                value={formData[field.key] || ''}
                onChange={(e) => updateFormData(field.key, e.target.value)}
                className="h-8 text-sm"
              />
            </div>
          ))}

          {/* Calculate Button */}
          <div className="flex gap-2">
            <Button 
              onClick={calculateDosage} 
              disabled={loading}
              size="sm"
              className="flex-1 h-8 text-xs"
            >
              {loading ? 'Calculating...' : 'Calculate'}
            </Button>
            <Button 
              variant="outline" 
              size="sm"
              onClick={resetCalculator}
              className="h-8 px-2"
            >
              <RotateCcw className="h-3 w-3" />
            </Button>
          </div>

          {/* Error Display */}
          {error && (
            <div className="text-red-600 text-xs bg-red-50 p-2 rounded border border-red-200">
              {error}
            </div>
          )}

          {/* Results Display */}
          {result && (
            <div className="bg-white border rounded p-3 space-y-2">
              <div className="font-medium text-xs text-gray-900 mb-2">Results:</div>

              <div className="grid grid-cols-2 gap-2 text-xs">
                <div className="bg-gray-50 p-2 rounded">
                  <div className="text-gray-600">Concentration</div>
                  <div className="font-semibold">
                    {result.concentration} mg/ml
                    {result.concentrationMcg && (
                      <div className="text-gray-500">({result.concentrationMcg} mcg/ml)</div>
                    )}
                  </div>
                </div>

                <div className="bg-gray-50 p-2 rounded">
                  <div className="text-gray-600">Injection Volume</div>
                  <div className="font-semibold">{result.injectionVolume} ml</div>
                </div>

                {result.unitsPerML && (
                  <div className="bg-gray-50 p-2 rounded">
                    <div className="text-gray-600">Units per ML</div>
                    <div className="font-semibold">{result.unitsPerML} mcg/ml</div>
                  </div>
                )}

                <div className="bg-gray-50 p-2 rounded">
                  <div className="text-gray-600">Doses per Vial</div>
                  <div className="font-semibold">{result.dosesPerVial}</div>
                </div>
              </div>

              <div className="flex items-center justify-between pt-2 border-t">
                <Badge variant="outline" className="text-xs">
                  <Info className="h-3 w-3 mr-1" />
                  Research Use Only
                </Badge>
                <div className="text-xs text-gray-500">
                  {result.doseUnit || 'mcg'} dosing
                </div>
              </div>
            </div>
          )}
        </CardContent>
      )}
    </Card>
  );
};

export default MiniCalculator;

frontend/src/components/calculators/Univ.content:
import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/card';
import { Button } from '../ui/button';
import { Input } from '../ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../ui/select';
import { Calculator, Info, Save, RotateCcw, AlertTriangle, Zap } from 'lucide-react';
import { Badge } from '../ui/badge';

const UniversalCalculator = ({ peptide, onCalculate }) => {
  const [formData, setFormData] = useState({});
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [calculationType, setCalculationType] = useState('reconstitution');

  // Load saved inputs for this peptide
  useEffect(() => {
    const savedData = localStorage.getItem(`calculator-${peptide.slug}-${calculationType}`);
    if (savedData) {
      try {
        setFormData(JSON.parse(savedData));
      } catch (e) {
        console.error('Error loading saved calculator data:', e);
      }
    }
  }, [peptide.slug, calculationType]);

  // Save inputs whenever form data changes
  useEffect(() => {
    if (Object.keys(formData).length > 0) {
      localStorage.setItem(`calculator-${peptide.slug}-${calculationType}`, JSON.stringify(formData));
    }
  }, [formData, peptide.slug, calculationType]);

  const updateFormData = (key, value) => {
    setFormData(prev => ({ ...prev, [key]: value }));
  };

  const calculateDosage = async () => {
    if (!validateInputs()) return;

    setLoading(true);
    setError(null);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 500));

      const calculatedResult = performCalculation();
      setResult(calculatedResult);

      // Emit analytics event
      if (onCalculate) {
        onCalculate({
          peptide: peptide.slug,
          type: calculationType,
          inputs: formData,
          result: calculatedResult
        });
      }

    } catch (err) {
      setError('Calculation failed. Please try again.');
      console.error('Calculator error:', err);
    } finally {
      setLoading(false);
    }
  };

  const validateInputs = () => {
    const config = getCalculatorConfig();
    for (const field of config.requiredFields) {
      if (!formData[field.key] || formData[field.key] <= 0) {
        setError(`Please enter a valid ${field.label.toLowerCase()}`);
        return false;
      }
    }
    return true;
  };

  const performCalculation = () => {
    const config = getCalculatorConfig();
    return config.calculate(formData);
  };

  const getCalculatorConfig = () => {
    const configs = {
      reconstitution: {
        title: `${peptide.name} Reconstitution Calculator`,
        requiredFields: [
          { key: 'vialSize', label: 'Vial Size (mg)', type: 'number', placeholder: '5' },
          { key: 'bacteriostaticWater', label: 'Bacteriostatic Water (ml)', type: 'number', placeholder: '2' },
          { key: 'targetDose', label: 'Target Dose', type: 'number', placeholder: getDoseUnit() === 'mg' ? '0.25' : '200', unit: getDoseUnit() }
        ],
        calculate: (data) => {
          const concentration = data.vialSize / data.bacteriostaticWater;

          if (getDoseUnit() === 'mg') {
            // For mg-based peptides (GLP-1 agonists, etc.)
            const injectionVolume = data.targetDose / concentration;
            const unitsPerML = concentration * 1000;

            return {
              concentration: concentration.toFixed(2),
              injectionVolume: injectionVolume.toFixed(3),
              unitsPerML: unitsPerML.toFixed(0),
              dosesPerVial: Math.floor(data.vialSize / data.targetDose),
              unit: 'mg/ml'
            };
          } else {
            // For mcg-based peptides (growth factors, etc.)
            const concentrationMcg = concentration * 1000;
            const injectionVolume = data.targetDose / concentrationMcg;

            return {
              concentration: concentration.toFixed(2),
              concentrationMcg: concentrationMcg.toFixed(0),
              injectionVolume: injectionVolume.toFixed(3),
              dosesPerVial: Math.floor((data.vialSize * 1000) / data.targetDose),
              unit: 'mg/ml'
            };
          }
        }
      },
      dosing: {
        title: `${peptide.name} Dosing Calculator`,
        requiredFields: [
          { key: 'bodyWeight', label: 'Body Weight (kg)', type: 'number', placeholder: '70' },
          { key: 'frequency', label: 'Frequency (times/day)', type: 'number', placeholder: '1' },
          { key: 'duration', label: 'Duration (weeks)', type: 'number', placeholder: '4' }
        ],
        calculate: (data) => {
          const baseDose = getBaseDoseForPeptide();
          const dailyDose = baseDose * data.bodyWeight * data.frequency;
          const weeklyDose = dailyDose * 7;
          const totalDose = weeklyDose * data.duration;

          return {
            dailyDose: dailyDose.toFixed(2),
            weeklyDose: weeklyDose.toFixed(2),
            totalDose: totalDose.toFixed(2),
            vialsNeeded: Math.ceil(totalDose / 5), // Assuming 5mg vials
            unit: getDoseUnit()
          };
        }
      },
      conversion: {
        title: `${peptide.name} Unit Converter`,
        requiredFields: [
          { key: 'amount', label: 'Amount', type: 'number', placeholder: '1' },
          { key: 'fromUnit', label: 'From Unit', type: 'select', options: ['mg', 'mcg', 'IU', 'ml'] },
          { key: 'toUnit', label: 'To Unit', type: 'select', options: ['mg', 'mcg', 'IU', 'ml'] }
        ],
        calculate: (data) => {
          let result = data.amount;

          // Convert to base unit (mg)
          switch (data.fromUnit) {
            case 'mcg':
              result = result / 1000;
              break;
            case 'IU':
              result = result * getIUConversion(); // Peptide-specific IU conversion
              break;
          }

          // Convert from base unit to target
          switch (data.toUnit) {
            case 'mcg':
              result = result * 1000;
              break;
            case 'IU':
              result = result / getIUConversion();
              break;
          }

          return {
            convertedAmount: result.toFixed(3),
            fromUnit: data.fromUnit,
            toUnit: data.toUnit,
            originalAmount: data.amount
          };
        }
      }
    };

    return configs[calculationType] || configs.reconstitution;
  };

  const getDoseUnit = () => {
    // Determine if peptide uses mg or mcg dosing
    const mgPeptides = ['semaglutide', 'tirzepatide', 'retatrutide', 'mazdutide', 'survodutide'];
    return mgPeptides.includes(peptide.slug) ? 'mg' : 'mcg';
  };

  const getBaseDoseForPeptide = () => {
    // Return base dose per kg for dosing calculations
    const dosing = {
      'semaglutide': 0.25, // mg
      'tirzepatide': 2.5,
      'bpc-157': 0.004, // mcg per kg
      'tb-500': 0.04,
      'ipamorelin': 3,
      'igf-1-lr3': 0.6
    };
    return dosing[peptide.slug] || 0.1;
  };

  const getIUConversion = () => {
    // IU to mg conversion factors (peptide-specific)
    const conversions = {
      'hgh': 0.33, // 1 IU = ~0.33mg
      'insulin': 0.0347
    };
    return conversions[peptide.slug] || 1;
  };

  const resetCalculator = () => {
    setFormData({});
    setResult(null);
    setError(null);
    localStorage.removeItem(`calculator-${peptide.slug}-${calculationType}`);
  };

  const config = getCalculatorConfig();

  return (
    <Card className="border-blue-200 bg-gradient-to-br from-blue-50 to-indigo-50">
      <CardHeader className="pb-3">
        <CardTitle className="text-sm font-medium flex items-center text-blue-900">
          <Calculator className="h-4 w-4 mr-2" />
          {config.title}
        </CardTitle>
        <div className="flex gap-2 mt-2">
          <Select value={calculationType} onValueChange={setCalculationType}>
            <SelectTrigger className="h-7 text-xs">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="reconstitution">Reconstitution</SelectItem>
              <SelectItem value="dosing">Dosing Protocol</SelectItem>
              <SelectItem value="conversion">Unit Converter</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </CardHeader>

      <CardContent className="pt-0 space-y-3">
        {/* Input Fields */}
        <div className="grid grid-cols-1 gap-2">
          {config.requiredFields.map((field) => (
            <div key={field.key}>
              <label className="text-xs text-gray-600 mb-1 block">
                {field.label} {field.unit && <span className="text-gray-400">({field.unit})</span>}
              </label>
              {field.type === 'select' ? (
                <Select 
                  value={formData[field.key] || ''} 
                  onValueChange={(value) => updateFormData(field.key, value)}
                >
                  <SelectTrigger className="h-8 text-sm">
                    <SelectValue placeholder={`Select ${field.label.toLowerCase()}`} />
                  </SelectTrigger>
                  <SelectContent>
                    {field.options.map(option => (
                      <SelectItem key={option} value={option}>{option}</SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              ) : (
                <Input
                  type={field.type}
                  placeholder={field.placeholder}
                  value={formData[field.key] || ''}
                  onChange={(e) => updateFormData(field.key, parseFloat(e.target.value) || '')}
                  className="h-8 text-sm"
                />
              )}
            </div>
          ))}
        </div>

        {/* Action Buttons */}
        <div className="flex gap-2">
          <Button 
            onClick={calculateDosage} 
            disabled={loading}
            size="sm"
            className="flex-1 h-8 text-xs bg-blue-600 hover:bg-blue-700"
          >
            {loading ? (
              <div className="flex items-center">
                <div className="animate-spin h-3 w-3 border border-white border-t-transparent rounded-full mr-2"></div>
                Calculating...
              </div>
            ) : (
              <>
                <Zap className="h-3 w-3 mr-1" />
                Calculate
              </>
            )}
          </Button>
          <Button 
            variant="outline" 
            size="sm"
            onClick={resetCalculator}
            className="h-8 px-2"
          >
            <RotateCcw className="h-3 w-3" />
          </Button>
        </div>

        {/* Error Display */}
        {error && (
          <div className="text-red-600 text-xs bg-red-50 p-2 rounded border border-red-200 flex items-center">
            <AlertTriangle className="h-3 w-3 mr-1 flex-shrink-0" />
            {error}
          </div>
        )}

        {/* Results Display */}
        {result && (
          <div className="bg-white border rounded-lg p-3 space-y-2 shadow-sm">
            <div className="text-xs font-medium text-gray-900 mb-2 flex items-center">
              <Info className="h-3 w-3 mr-1" />
              Results:
            </div>

            {calculationType === 'reconstitution' && (
              <div className="grid grid-cols-2 gap-2 text-xs">
                <div>
                  <span className="text-gray-600">Concentration:</span>
                  <div className="font-medium text-blue-900">{result.concentration} {result.unit}</div>
                </div>
                <div>
                  <span className="text-gray-600">Injection Volume:</span>
                  <div className="font-medium text-blue-900">{result.injectionVolume} ml</div>
                </div>
                {result.concentrationMcg && (
                  <div>
                    <span className="text-gray-600">mcg/ml:</span>
                    <div className="font-medium text-blue-900">{result.concentrationMcg}</div>
                  </div>
                )}
                <div>
                  <span className="text-gray-600">Doses per vial:</span>
                  <div className="font-medium text-blue-900">{result.dosesPerVial}</div>
                </div>
              </div>
            )}

            {calculationType === 'dosing' && (
              <div className="grid grid-cols-2 gap-2 text-xs">
                <div>
                  <span className="text-gray-600">Daily Dose:</span>
                  <div className="font-medium text-blue-900">{result.dailyDose} {result.unit}</div>
                </div>
                <div>
                  <span className="text-gray-600">Weekly Dose:</span>
                  <div className="font-medium text-blue-900">{result.weeklyDose} {result.unit}</div>
                </div>
                <div>
                  <span className="text-gray-600">Total Needed:</span>
                  <div className="font-medium text-blue-900">{result.totalDose} {result.unit}</div>
                </div>
                <div>
                  <span className="text-gray-600">Vials Needed:</span>
                  <div className="font-medium text-blue-900">{result.vialsNeeded} vials</div>
                </div>
              </div>
            )}

            {calculationType === 'conversion' && (
              <div className="text-center">
                <div className="text-sm">
                  <span className="font-medium text-blue-900">{result.originalAmount} {result.fromUnit}</span>
                  <span className="text-gray-500 mx-2">=</span>
                  <span className="font-bold text-blue-900">{result.convertedAmount} {result.toUnit}</span>
                </div>
              </div>
            )}

            <div className="flex items-center justify-between pt-2 border-t">
              <Badge variant="outline" className="text-xs border-orange-300 text-orange-700">
                <AlertTriangle className="h-3 w-3 mr-1" />
                Research Use Only
              </Badge>
              <Button 
                variant="ghost" 
                size="sm"
                onClick={() => {
                  // Save calculation to history
                  const timestamp = new Date().toISOString();
                  const calculationHistory = JSON.parse(localStorage.getItem('calculation-history') || '[]');
                  calculationHistory.push({
                    peptide: peptide.name,
                    type: calculationType,
                    timestamp,
                    inputs: formData,
                    result
                  });
                  localStorage.setItem('calculation-history', JSON.stringify(calculationHistory.slice(-20))); // Keep last 20
                }}
                className="h-6 px-2 text-blue-700 hover:bg-blue-100"
              >
                <Save className="h-3 w-3" />
              </Button>
            </div>
          </div>
        )}

        {/* WADA Warning for banned peptides */}
        {peptide.wadaBanned && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-2">
            <div className="text-xs text-red-700 font-medium">
              ðŸ›‘ WADA BANNED - This peptide is prohibited in competitive sports
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
};

export default UniversalCalculator;

