import React, { useState, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/card';
import { Button } from '../ui/button';
import { Badge } from '../ui/badge';
import { 
  FileText, 
  Upload, 
  Download, 
  Calendar, 
  CheckCircle2, 
  AlertTriangle,
  Clock,
  Trash2
} from 'lucide-react';

const COATab = ({ peptide, onCOAUpdate }) => {
  const [coaFiles, setCOAFiles] = useState(() => {
    // Initialize with some sample COAs for demonstration
    const sampleCOAs = {
      'semaglutide': [
        {
          id: 1,
          lotNumber: 'SEM-2024-001',
          fileName: 'Semaglutide_COA_SEM-2024-001.pdf',
          uploadDate: '2024-01-15',
          status: 'current',
          fileSize: '2.3 MB'
        }
      ],
      'tirzepatide': [
        {
          id: 2,
          lotNumber: 'TIR-2024-002',
          fileName: 'Tirzepatide_COA_TIR-2024-002.pdf',
          uploadDate: '2024-01-20',
          status: 'current',
          fileSize: '1.8 MB'
        }
      ],
      'igf-1-lr3': [
        {
          id: 3,
          lotNumber: 'IGF-2024-003',
          fileName: 'IGF1-LR3_COA_IGF-2024-003.pdf',
          uploadDate: '2024-01-25',
          status: 'current',
          fileSize: '2.1 MB'
        }
      ]
    };

    return sampleCOAs[peptide.slug] || [];
  });

  const [isUploading, setIsUploading] = useState(false);
  const fileInputRef = useRef(null);

  const handleFileUpload = async (event) => {
    const files = Array.from(event.target.files);
    if (files.length === 0) return;

    setIsUploading(true);

    // Simulate file upload process
    try {
      await new Promise(resolve => setTimeout(resolve, 2000));

      const newCOAs = files.map((file, index) => ({
        id: Date.now() + index,
        lotNumber: `${peptide.slug.toUpperCase()}-${new Date().getFullYear()}-${String(Math.floor(Math.random() * 999) + 1).padStart(3, '0')}`,
        fileName: file.name,
        uploadDate: new Date().toISOString().split('T')[0],
        status: 'current',
        fileSize: `${(file.size / (1024 * 1024)).toFixed(1)} MB`
      }));

      setCOAFiles(prev => [...prev, ...newCOAs]);

      // Analytics event
      if (window.gtag) {
        window.gtag('event', 'coa_upload', {
          peptide: peptide.slug,
          file_count: files.length,
          page_title: `${peptide.name} COA Upload`,
          custom_parameters: {
            peptide_handle: peptide.slug,
            upload_count: files.length
          }
        });
      }

      if (onCOAUpdate) {
        onCOAUpdate(peptide.slug, newCOAs.length > 0);
      }

    } catch (error) {
      console.error('Upload failed:', error);
    } finally {
      setIsUploading(false);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  const handleDownload = (coa) => {
    // Simulate PDF download
    const link = document.createElement('a');
    link.href = `#download-${coa.id}`;
    link.download = coa.fileName;
    link.click();

    // Analytics event
    if (window.gtag) {
      window.gtag('event', 'coa_download', {
        peptide: peptide.slug,
        lot_number: coa.lotNumber,
        page_title: `${peptide.name} COA Download`,
        custom_parameters: {
          peptide_handle: peptide.slug,
          lot_number: coa.lotNumber
        }
      });
    }
  };

  const handleDelete = (coaId) => {
    setCOAFiles(prev => prev.filter(coa => coa.id !== coaId));

    const remainingCOAs = coaFiles.filter(coa => coa.id !== coaId);
    if (onCOAUpdate) {
      onCOAUpdate(peptide.slug, remainingCOAs.length > 0);
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'current':
        return <CheckCircle2 className="h-4 w-4 text-green-600" />;
      case 'expiring':
        return <AlertTriangle className="h-4 w-4 text-yellow-600" />;
      case 'expired':
        return <Clock className="h-4 w-4 text-red-600" />;
      default:
        return <FileText className="h-4 w-4 text-gray-600" />;
    }
  };

  const getStatusBadge = (status) => {
    const configs = {
      current: { color: 'bg-green-100 text-green-800 border-green-300', text: 'Current' },
      expiring: { color: 'bg-yellow-100 text-yellow-800 border-yellow-300', text: 'Expiring Soon' },
      expired: { color: 'bg-red-100 text-red-800 border-red-300', text: 'Expired' }
    };

    const config = configs[status] || configs.current;

    return (
      <Badge className={`${config.color} text-xs font-medium`}>
        {config.text}
      </Badge>
    );
  };

  return (
    <div className="space-y-6">
      {/* COA Header */}
      <Card className="border-blue-200 bg-blue-50">
        <CardHeader className="pb-3">
          <CardTitle className="text-lg font-semibold text-blue-900 flex items-center">
            <FileText className="h-5 w-5 mr-2" />
            Certificates of Analysis (COA)
          </CardTitle>
          <p className="text-sm text-blue-700">
            Access and manage quality certificates for {peptide.name} research batches
          </p>
        </CardHeader>
      </Card>

      {/* Upload Section */}
      <Card>
        <CardContent className="p-6">
          <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
            <input
              ref={fileInputRef}
              type="file"
              multiple
              accept=".pdf,.doc,.docx"
              onChange={handleFileUpload}
              className="hidden"
              id="coa-upload"
            />
            <Upload className="h-8 w-8 text-gray-400 mx-auto mb-3" />
            <h3 className="text-sm font-medium text-gray-900 mb-1">
              Upload COA Documents
            </h3>
            <p className="text-xs text-gray-500 mb-4">
              PDF, DOC, or DOCX files up to 10MB each
            </p>
            <Button
              onClick={() => fileInputRef.current?.click()}
              disabled={isUploading}
              className="relative"
              aria-label="Upload COA files"
            >
              {isUploading ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Uploading...
                </>
              ) : (
                <>
                  <Upload className="h-4 w-4 mr-2" />
                  Select Files
                </>
              )}
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* COA Files Table */}
      {coaFiles.length > 0 ? (
        <Card>
          <CardHeader>
            <CardTitle className="text-md font-medium">
              Available COA Documents ({coaFiles.length})
            </CardTitle>
          </CardHeader>
          <CardContent className="p-0">
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50 border-b">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Lot Number
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      File Name
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Upload Date
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Status
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Size
                    </th>
                    <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {coaFiles.map((coa) => (
                    <tr key={coa.id} className="hover:bg-gray-50">
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center">
                          {getStatusIcon(coa.status)}
                          <span className="ml-2 text-sm font-medium text-gray-900">
                            {coa.lotNumber}
                          </span>
                        </div>
                      </td>
                      <td className="px-6 py-4">
                        <div className="text-sm text-gray-900">{coa.fileName}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center text-sm text-gray-500">
                          <Calendar className="h-4 w-4 mr-1" />
                          {new Date(coa.uploadDate).toLocaleDateString()}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {getStatusBadge(coa.status)}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {coa.fileSize}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                        <div className="flex items-center justify-end space-x-2">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleDownload(coa)}
                            aria-label={`Download COA ${coa.lotNumber}`}
                          >
                            <Download className="h-3 w-3" />
                          </Button>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleDelete(coa.id)}
                            className="text-red-600 hover:text-red-700 hover:bg-red-50"
                            aria-label={`Delete COA ${coa.lotNumber}`}
                          >
                            <Trash2 className="h-3 w-3" />
                          </Button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </CardContent>
        </Card>
      ) : (
        <Card>
          <CardContent className="p-8 text-center">
            <FileText className="h-12 w-12 text-gray-300 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              No COA Documents Available
            </h3>
            <p className="text-sm text-gray-500 mb-4">
              Upload certificates of analysis for {peptide.name} research batches
            </p>
            <Button
              onClick={() => fileInputRef.current?.click()}
              variant="outline"
            >
              <Upload className="h-4 w-4 mr-2" />
              Upload First COA
            </Button>
          </CardContent>
        </Card>
      )}

      {/* COA Information */}
      <Card className="border-gray-200 bg-gray-50">
        <CardContent className="p-4">
          <div className="text-xs text-gray-600">
            <h4 className="font-medium text-gray-900 mb-2">About COA Documents</h4>
            <ul className="space-y-1">
              <li>â€¢ COA certificates verify the purity, potency, and quality of research compounds</li>
              <li>â€¢ Documents should include batch/lot numbers, testing dates, and laboratory results</li>
              <li>â€¢ All research compounds should have current, valid COAs for quality assurance</li>
              <li>â€¢ Files are securely stored and only accessible to authorized research personnel</li>
            </ul>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default COATab;

