    const checkInteractions = () => {
      const warningMessages = [];

      // LL-37 + GHK-Cu interaction
      if (peptide.slug === 'll-37' && compareList.some(p => p.slug === 'ghk-cu')) {
        warningMessages.push("âš ï¸ LL-37 + GHK-Cu: Warn in open lesions");
      }
      if (peptide.slug === 'ghk-cu' && compareList.some(p => p.slug === 'll-37')) {
        warningMessages.push("âš ï¸ GHK-Cu + LL-37: Warn in open lesions");
      }

      // GHRP-6 + Ipamorelin interaction
      if (peptide.slug === 'ghrp-6' && compareList.some(p => p.slug === 'ipamorelin')) {
        warningMessages.push("âš ï¸ GHRP-6 + Ipamorelin: Warn on edema risk");
      }
      if (peptide.slug === 'ipamorelin' && compareList.some(p => p.slug === 'ghrp-6')) {
        warningMessages.push("âš ï¸ Ipamorelin + GHRP-6: Warn on edema risk");
      }

      // AOD9604 + Retatrutide interaction
      if (peptide.slug === 'aod9604' && compareList.some(p => p.slug === 'retatrutide')) {
        warningMessages.push("âš ï¸ AOD9604 + Retatrutide: Warn redundant fat loss pathways");
      }
      if (peptide.slug === 'retatrutide' && compareList.some(p => p.slug === 'aod9604')) {
        warningMessages.push("âš ï¸ Retatrutide + AOD9604: Warn redundant fat loss pathways");
      }

      setWarnings(warningMessages);
    };

    checkInteractions();
  }, [peptide, compareList]);

  if (warnings.length === 0) return null;

  return (
    <div className="mb-4">
      {warnings.map((warning, index) => (
        <div key={index} className="bg-red-50 border border-red-200 rounded-lg p-3 text-red-800 text-sm">
          <div className="flex items-center">
            <AlertTriangle className="h-4 w-4 mr-2" />
            {warning}
          </div>
        </div>
      ))}
    </div>
  );
};

const PeptidesSectionEnhanced = () => {
  const { peptideCategories, loading, error } = useData();
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedPeptide, setSelectedPeptide] = useState(null);
  const [activePDPTab, setActivePDPTab] = useState('overview');

  // Enhanced search and filter states
  const [searchTerm, setSearchTerm] = useState('');
  const [fdaFilter, setFdaFilter] = useState('all');
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [routeFilter, setRouteFilter] = useState('all');
  const [benefitFilter, setBenefitFilter] = useState('all');

  // Compare and Save functionality
  const [compareList, setCompareList] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [showInteractionChecker, setShowInteractionChecker] = useState(false);
  const [coaCounts, setCOACounts] = useState({});

  // Load saved data from localStorage
  useEffect(() => {
    const savedCompareList = localStorage.getItem('peptide-compare-list');
    const savedFavorites = localStorage.getItem('peptide-favorites');

    if (savedCompareList) {
      try {
        setCompareList(JSON.parse(savedCompareList));
      } catch (e) {
        console.error('Error loading compare list:', e);
      }
    }

    if (savedFavorites) {
      try {
        setFavorites(JSON.parse(savedFavorites));
      } catch (e) {
        console.error('Error loading favorites:', e);
      }
    }
  }, []);

  // Save compare list to localStorage
  useEffect(() => {
    localStorage.setItem('peptide-compare-list', JSON.stringify(compareList));
  }, [compareList]);

  // Save favorites to localStorage
  useEffect(() => {
    localStorage.setItem('peptide-favorites', JSON.stringify(favorites));
  }, [favorites]);

  // Analytics event handler for calculator
  const handleCalculatorEvent = (data) => {
    // Emit analytics event for calculator usage
    if (typeof window !== 'undefined' && window.gtag) {
      window.gtag('event', 'calculator_submit', {
        peptide: data.peptide,
        page_title: `${data.peptide} Calculator`,
        custom_parameters: {
          peptide_handle: data.peptide,
          calculation_type: 'dosage'
        }
      });
    }
    console.log('Calculator event:', data);
  };

  // COA update handler
  const handleCOAUpdate = (peptideSlug, hasCOA) => {
    setCOACounts(prev => ({
      ...prev,
      [peptideSlug]: hasCOA ? 1 : 0
    }));
  };

  // Get all peptides for search
  const allPeptides = Object.entries(peptideCategories || {}).flatMap(([categoryKey, category]) =>
    (category.peptides || []).map(peptide => ({
      ...peptide,
      categoryKey,
      categoryTitle: category.title
    }))
  );

  // Enhanced search with synonyms
  const searchPeptides = (peptides, term) => {
    if (!term) return peptides;

    const normalizedTerm = term.toLowerCase().trim();
    const synonymSlug = synonymMap[normalizedTerm];

    return peptides.filter(peptide => {
      // Direct name match
      if (peptide.name.toLowerCase().includes(normalizedTerm)) return true;

      // Alias match
      if (peptide.aliases && peptide.aliases.some(alias => 
        alias.toLowerCase().includes(normalizedTerm)
      )) return true;

      // Synonym match
      if (synonymSlug && peptide.slug === synonymSlug) return true;

      // Benefits match
      if (peptide.benefits && peptide.benefits.some(benefit =>
        benefit.toLowerCase().includes(normalizedTerm)
      )) return true;

      // Category match
      if (peptide.categoryTitle && peptide.categoryTitle.toLowerCase().includes(normalizedTerm)) return true;

      return false;
    });
  };

  // Apply all filters
  const getFilteredPeptides = () => {
    let filtered = allPeptides;

    // Search filter
    filtered = searchPeptides(filtered, searchTerm);

    // FDA status filter
    if (fdaFilter !== 'all') {
      if (fdaFilter === 'approved') {
        filtered = filtered.filter(p => p.fdaApproved === true);
      } else if (fdaFilter === 'clinical-trials') {
        filtered = filtered.filter(p => p.researchStatus && p.researchStatus.includes('Clinical Trials'));
      } else if (fdaFilter === 'research-only') {
        filtered = filtered.filter(p => !p.fdaApproved && (!p.researchStatus || !p.researchStatus.includes('Clinical Trials')));
      }
    }

    // Category filter
    if (categoryFilter !== 'all') {
      filtered = filtered.filter(p => p.categoryKey === categoryFilter);
    }

    // Route filter
    if (routeFilter !== 'all') {
      filtered = filtered.filter(p => p.routes && p.routes.includes(routeFilter));
    }

    // Benefit filter
    if (benefitFilter !== 'all') {
      filtered = filtered.filter(p => p.benefits && p.benefits.some(benefit => 
        benefit.toLowerCase().includes(benefitFilter.toLowerCase())
      ));
    }

    return filtered;
  };

  const filteredPeptides = getFilteredPeptides();

  // Compare functionality
  const toggleCompare = (peptide) => {
    setCompareList(prev => {
      const exists = prev.find(p => p.slug === peptide.slug);
      if (exists) {
        // Emit remove analytics event
        if (window.gtag) {
          window.gtag('event', 'compare_remove', {
            peptide: peptide.slug,
            page_title: 'Peptide Database',
            custom_parameters: {
              peptide_handle: peptide.slug,
              action: 'remove'
            }
          });
        }
        return prev.filter(p => p.slug !== peptide.slug);
      } else if (prev.length < 4) { // Max 4 items as requested
        // Emit add analytics event
        if (window.gtag) {
          window.gtag('event', 'compare_add', {
            peptide: peptide.slug,
            page_title: 'Peptide Database',
            custom_parameters: {
              peptide_handle: peptide.slug,
              action: 'add'
            }
          });
        }
        return [...prev, peptide];
      }
      return prev;
    });
