                            {selectedPeptide.athleteWarning && (
                              <p className="text-sm text-red-700">{selectedPeptide.athleteWarning}</p>
                            )}
                          </div>
                        )}

                        {selectedPeptide.athleteWarning && !selectedPeptide.wadaBanned && (
                          <div className="mt-3 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                            <p className="text-yellow-800 font-medium mb-1">âš ï¸ Athlete Notice</p>
                            <p className="text-sm text-yellow-700">{selectedPeptide.athleteWarning}</p>
                          </div>
                        )}
                      </div>
                    )}

                    {selectedPeptide.description && (
                      <div>
                        <h4 className="font-semibold text-gray-900 mb-2">Description</h4>
                        <p className="text-gray-700">{selectedPeptide.description}</p>
                      </div>
                    )}

                    {selectedPeptide.mechanism && (
                      <div>
                        <h4 className="font-semibold text-gray-900 mb-2">Mechanism of Action</h4>
                        <p className="text-gray-700">{selectedPeptide.mechanism}</p>
                      </div>
                    )}

                    {selectedPeptide.benefits && selectedPeptide.benefits.length > 0 && (
                      <div>
                        <h4 className="font-semibold text-gray-900 mb-3">Key Benefits</h4>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
                          {selectedPeptide.benefits.map((benefit, index) => (
                            <div key={index} className="flex items-center text-sm text-gray-700">
                              <div className="w-2 h-2 bg-blue-500 rounded-full mr-2"></div>
                              {benefit}
                            </div>
                          ))}
                        </div>
                      </div>
                    )}

                    {selectedPeptide.routes && (
                      <div>
                        <h4 className="font-semibold text-gray-900 mb-2">Route of Administration</h4>
                        <div className="flex flex-wrap gap-2">
                          {selectedPeptide.routes.map((route, index) => (
                            <Badge key={index} variant="outline" className="text-sm">
                              {route}
                            </Badge>
                          ))}
                        </div>
                      </div>
                    )}

                    {selectedPeptide.dosage && (
                      <div>
                        <h4 className="font-semibold text-gray-900 mb-2">
                          {selectedPeptide.fdaApproved ? "Clinical Dosage" : "Research Protocols"}
                        </h4>
                        <div className={`border rounded-lg p-3 ${
                          selectedPeptide.fdaApproved 
                            ? "bg-blue-50 border-blue-200" 
                            : "bg-yellow-50 border-yellow-200"
                        }`}>
                          <p className={`text-sm ${
                            selectedPeptide.fdaApproved ? "text-blue-800" : "text-yellow-800"
                          }`}>
                            {selectedPeptide.dosage}
                          </p>
                          <p className={`text-xs mt-1 font-medium ${
                            selectedPeptide.fdaApproved ? "text-blue-600" : "text-yellow-600"
                          }`}>
                            {selectedPeptide.fdaApproved 
                              ? "* Consult healthcare provider for proper prescription and monitoring."
                              : "* For research purposes only. Not approved for human consumption."
                            }
                          </p>
                        </div>
                      </div>
                    )}

                    {/* E-E-A-T Blocks */}
                    <EATBlocks peptide={selectedPeptide} />
                  </div>
                </>
              )}

              {activePDPTab === 'calculator' && (
                <DosageCalculatorMount peptide={selectedPeptide} onCalculate={handleCalculatorEvent} />
              )}

              {activePDPTab === 'coa' && (
                <COATab peptide={selectedPeptide} onCOAUpdate={handleCOAUpdate} />
              )}

              {activePDPTab === 'share' && (
                <div className="space-y-6">
                  <SocialShareButtons 
                    peptide={selectedPeptide}
                    url={`https://www.professorpeptides.org/peptide/${selectedPeptide.slug}`}
                    title={`${selectedPeptide.name} - Research Peptide Information`}
                    description={selectedPeptide.description}
                  />
                </div>
              )}
            </div>

            <div className="flex gap-3 pt-4 border-t mt-4 flex-shrink-0">
              <Button className="flex-1 bg-blue-600 hover:bg-blue-700">
                {selectedPeptide.fdaApproved ? "Clinical Studies" : "Research Data"}
              </Button>
              <Button variant="outline" className="flex-1">
                Safety Profile
              </Button>
            </div>
          </DialogContent>
        </Dialog>
      )}

      {/* Interaction Checker */}
      <InteractionChecker 
        open={showInteractionChecker} 
        onClose={() => setShowInteractionChecker(false)} 
      />
    </>
  );
};

