frontend/src/components/navigation/Bread.content:
import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { ChevronRight, Home } from 'lucide-react';
import { getAllPeptides, peptideCategories } from '../../data/peptideData';

/**
 * Professional Breadcrumb Navigation Component
 * Generates SEO-friendly breadcrumb navigation for all page types
 * Includes structured data for enhanced search engine visibility
 */
const BreadcrumbNavigation = ({ 
  customBreadcrumbs = null,
  showHome = true,
  className = '',
  variant = 'default' // default, compact, pills
}) => {
  const location = useLocation();

  // Generate breadcrumbs based on current path
  const generateBreadcrumbs = () => {
    if (customBreadcrumbs) return customBreadcrumbs;

    const pathSegments = location.pathname.split('/').filter(segment => segment);
    const breadcrumbs = [];

    // Always start with home if showHome is true
    if (showHome) {
      breadcrumbs.push({
        label: 'Home',
        path: '/',
        icon: Home
      });
    }

    // Build breadcrumbs based on path structure
    let currentPath = '';

    pathSegments.forEach((segment, index) => {
      currentPath += `/${segment}`;
      const isLast = index === pathSegments.length - 1;

      let breadcrumb = null;

      // Route-specific breadcrumb generation
      switch (segment) {
        case 'peptides':
          if (pathSegments.length === 1) {
            // /peptides
            breadcrumb = {
              label: 'Peptides',
              path: '/peptides',
              description: '47+ Research Peptides'
            };
          } else if (pathSegments.length === 2) {
            // /peptides/categoryKey
            const categoryKey = pathSegments[1];
            const category = peptideCategories[categoryKey];
            if (category) {
              breadcrumb = {
                label: category.title,
                path: currentPath,
                description: `${category.peptides?.length || 0} peptides`
              };
            }
          }
          break;

        case 'peptide':
          if (pathSegments.length === 2) {
            // /peptide/slug
            const peptideSlug = pathSegments[1];
            const allPeptides = getAllPeptides();
            const peptide = allPeptides.find(p => p.slug === peptideSlug);
            if (peptide) {
              breadcrumb = {
                label: peptide.name,
                path: currentPath,
                description: peptide.fdaApproved ? 'FDA Approved' : 'Research Only'
              };
            }
          }
          break;

        case 'calculators':
          if (pathSegments.length === 1) {
            // /calculators
            breadcrumb = {
              label: 'Calculators',
              path: '/calculators',
              description: 'Professional Medical Tools'
            };
          } else {
            // /calculators/type
            const calcType = pathSegments[1];
            const calcLabels = {
              'bmi': 'BMI Calculator',
              'reconstitution': 'Reconstitution Calculator',
              'glp1': 'GLP-1 Calculator',
              'trt': 'TRT Calculator',
              'fitness': 'Fitness Calculator',
              'melanotan': 'Melanotan Calculator',
              'protocol': 'Protocol Tool'
            };

            breadcrumb = {
              label: calcLabels[calcType] || 'Calculator',
              path: currentPath,
              description: 'Free Professional Tool'
            };
          }
          break;

        case 'blog':
          if (pathSegments.length === 1) {
            // /blog
            breadcrumb = {
              label: 'Blog',
              path: '/blog',
              description: 'Research Insights & Updates'
            };
          } else {
            // /blog/slug - would need blog data lookup
            breadcrumb = {
              label: 'Article',
              path: currentPath,
              description: 'Research Article'
            };
          }
          break;

        case 'research':
          if (pathSegments.length === 1) {
            // /research
            breadcrumb = {
              label: 'Research',
              path: '/research',
              description: '190+ Scientific Citations'
            };
          } else {
            // /research/category
            const researchCategory = pathSegments[1];
            const categoryLabels = {
              'metabolic-health': 'Metabolic Health',
              'tissue-repair': 'Tissue Repair',
              'cognitive-enhancement': 'Cognitive Enhancement',
              'performance-enhancement': 'Performance Enhancement',
              'anti-aging': 'Anti-Aging',
              'immune-support': 'Immune Support',
              'pain-management': 'Pain Management'
            };

            breadcrumb = {
              label: categoryLabels[researchCategory] || 'Research Category',
              path: currentPath,
              description: 'Clinical Studies & Research'
            };
          }
          break;

        case 'about':
          breadcrumb = {
            label: 'About',
            path: '/about',
            description: 'Our Team & Mission'
          };
          break;

        case 'faq':
          breadcrumb = {
            label: 'FAQ',
            path: '/faq',
            description: 'Frequently Asked Questions'
          };
          break;

        case 'resources':
          breadcrumb = {
            label: 'Resources',
            path: '/resources',
            description: 'Guidelines & Safety Information'
          };
          break;

        case 'company':
          breadcrumb = {
            label: 'Company',
            path: '/company',
            description: 'Company Information'
          };
          break;

        case 'browse':
          if (pathSegments[0] === 'peptides') {
            breadcrumb = {
              label: 'Browse',
              path: currentPath,
              description: 'Interactive Database'
            };
          }
          break;

        case 'interactions':
          if (pathSegments[0] === 'peptides') {
            breadcrumb = {
              label: 'Interactions',
              path: currentPath,
              description: 'Drug Interaction Checker'
            };
          }
          break;

        case 'compatibility':
          if (pathSegments[0] === 'peptides') {
            breadcrumb = {
              label: 'Compatibility',
              path: currentPath,
              description: 'Peptide Compatibility Tool'
            };
          }
          break;

        default:
          // Generic segment handling
          breadcrumb = {
            label: segment.charAt(0).toUpperCase() + segment.slice(1).replace(/-/g, ' '),
            path: currentPath,
            description: ''
          };
      }

      if (breadcrumb) {
        breadcrumb.isCurrentPage = isLast;
        breadcrumbs.push(breadcrumb);
      }
    });

    return breadcrumbs;
  };

  const breadcrumbs = generateBreadcrumbs();

  // Don't render if no breadcrumbs or only home
  if (!breadcrumbs.length || (breadcrumbs.length === 1 && showHome)) {
    return null;
  }

  // Generate structured data for SEO
  const generateStructuredData = () => ({
    "@context": "https://schema.org",
    "@type": "BreadcrumbList",
    "itemListElement": breadcrumbs.map((crumb, index) => ({
      "@type": "ListItem",
      "position": index + 1,
      "name": crumb.label,
      "item": `https://www.professorpeptides.org${crumb.path}`
    }))
  });

  // Variant-specific styling
  const getVariantClasses = () => {
    switch (variant) {
      case 'compact':
        return {
          container: 'flex items-center space-x-1 text-sm',
          item: 'text-gray-600 hover:text-blue-600 transition-colors duration-200',
          current: 'text-gray-900 font-medium',
          separator: 'text-gray-400 mx-1'
        };
      case 'pills':
        return {
          container: 'flex items-center flex-wrap gap-2',
          item: 'px-3 py-1 bg-gray-100 text-gray-700 rounded-full hover:bg-blue-100 hover:text-blue-700 transition-all duration-200 text-sm',
          current: 'px-3 py-1 bg-blue-600 text-white rounded-full text-sm',
          separator: 'text-gray-400'
        };
      default:
        return {
          container: 'flex items-center space-x-2 text-sm md:text-base',
          item: 'text-gray-600 hover:text-blue-600 transition-colors duration-200 font-medium',
          current: 'text-gray-900 font-semibold',
          separator: 'text-gray-400 mx-2'
        };
    }
  };

  const styles = getVariantClasses();

  return (
    <>
      {/* Structured Data */}
      <script 
        type="application/ld+json" 
        dangerouslySetInnerHTML={{ __html: JSON.stringify(generateStructuredData()) }} 
      />

      {/* Breadcrumb Navigation */}
      <nav 
        aria-label="Breadcrumb" 
        className={`${styles.container} ${className}`}
        role="navigation"
      >
        <ol className="flex items-center space-x-2">
          {breadcrumbs.map((crumb, index) => (
            <li key={crumb.path} className="flex items-center">
              {index > 0 && variant !== 'pills' && (
                <ChevronRight 
                  size={16} 
                  className={styles.separator}
                  aria-hidden="true"
                />
              )}

              {crumb.isCurrentPage ? (
                <span 
                  className={styles.current}
                  aria-current="page"
                  title={crumb.description}
                >
                  {crumb.icon && <crumb.icon size={16} className="mr-1 inline" />}
                  {crumb.label}
                </span>
              ) : (
                <Link
                  to={crumb.path}
                  className={styles.item}
                  title={crumb.description}
                  aria-label={`Go to ${crumb.label}${crumb.description ? ` - ${crumb.description}` : ''}`}
                >
                  {crumb.icon && <crumb.icon size={16} className="mr-1 inline" />}
                  {crumb.label}
                </Link>
              )}
            </li>
          ))}
        </ol>
      </nav>
    </>
  );
};

// Higher-order component for pages that need breadcrumbs
export const withBreadcrumbs = (Component, breadcrumbConfig = {}) => {
  return function BreadcrumbWrapper(props) {
    return (
      <>
        <BreadcrumbNavigation {...breadcrumbConfig} />
        <Component {...props} />
      </>
    );
  };
};

// Hook for getting current breadcrumb data
export const useBreadcrumbs = () => {
  const location = useLocation();

  const getBreadcrumbData = () => {
    const pathSegments = location.pathname.split('/').filter(segment => segment);

    return {
      currentPage: pathSegments[pathSegments.length - 1] || 'home',
      depth: pathSegments.length,
      isRoot: pathSegments.length === 0,
      parentPath: pathSegments.length > 1 ? '/' + pathSegments.slice(0, -1).join('/') : '/'
    };
  };

  return getBreadcrumbData();
};

// Utility function to generate breadcrumb title for SEO
export const generateBreadcrumbTitle = (breadcrumbs) => {
  if (!breadcrumbs || breadcrumbs.length === 0) return '';

  return breadcrumbs
    .map(crumb => crumb.label)
    .reverse()
    .join(' | ') + ' | Professor Peptides';
};

export default BreadcrumbNavigation;

frontend/src/components/performance/Lazy.content:
import React, { useState, useRef, useEffect } from 'react';

/**
 * Lazy Image Component
 * Implements lazy loading with proper placeholder and error handling
 */
const LazyImage = ({ 
  src, 
  alt, 
  className = "", 
  placeholder = null,
  quality = 20,
  width,
  height,
  ...props 
}) => {
  const [isLoading, setIsLoading] = useState(true);
  const [hasError, setHasError] = useState(false);
  const [isIntersecting, setIsIntersecting] = useState(false);
  const imgRef = useRef();

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsIntersecting(true);
          observer.disconnect();
        }
      },
      {
        rootMargin: '50px 0px', // Load 50px before entering viewport
        threshold: 0.1
      }
    );

    if (imgRef.current) {
      observer.observe(imgRef.current);
    }

    return () => observer.disconnect();
  }, []);

  const handleLoad = () => {
    setIsLoading(false);
  };

  const handleError = () => {
    setIsLoading(false);
    setHasError(true);
  };

  const PlaceholderComponent = placeholder || (() => (
    <div className={`bg-gray-200 animate-pulse flex items-center justify-center ${className}`}>
      <div className="text-gray-400 text-sm">Loading...</div>
    </div>
  ));

  const ErrorComponent = () => (
    <div className={`bg-gray-100 border border-gray-300 flex items-center justify-center ${className}`}>
      <div className="text-gray-500 text-sm text-center p-4">
        <div className="text-2xl mb-2">ðŸ“·</div>
        <div>Image unavailable</div>
      </div>
    </div>
  );

  return (
    <div ref={imgRef} className="relative">
      {!isIntersecting && <PlaceholderComponent />}

      {isIntersecting && (
        <>
          {isLoading && <PlaceholderComponent />}
          {hasError && <ErrorComponent />}

          <img
            src={src}
            alt={alt}
            className={`${className} ${isLoading ? 'opacity-0' : 'opacity-100'} transition-opacity duration-300`}
            onLoad={handleLoad}
            onError={handleError}
            loading="lazy"
            width={width}
            height={height}
            style={{ display: hasError ? 'none' : 'block' }}
            {...props}
          />
        </>
      )}
    </div>
  );
};

export default LazyImage;

