 * for all page types in the Professor Peptides website
 */
const MetaTagsEnhanced = ({ 
  title, 
  description, 
  keywords, 
  canonical, 
  image, 
  type = 'website',
  peptide,
  calculator,
  blogPost,
  category,
  pageType = 'homepage',
