      });
    }

    return baseData;
  };

  // Generate breadcrumb structured data
  const generateBreadcrumbs = () => {
    const breadcrumbs = [
      { name: "Home", url: siteUrl }
    ];

    switch (pageType) {
      case 'peptide':
        breadcrumbs.push(
          { name: "Peptides", url: `${siteUrl}/peptides` },
          { name: peptide.categoryTitle || peptide.category, url: `${siteUrl}/peptides/${peptide.categoryKey}` },
          { name: peptide.name, url: seoData.canonical }
        );
        break;
      case 'calculator':
        breadcrumbs.push(
          { name: "Calculators", url: `${siteUrl}/calculators` },
          { name: calculator.name, url: seoData.canonical }
        );
        break;
      case 'blog':
        breadcrumbs.push(
          { name: "Blog", url: `${siteUrl}/blog` },
          { name: blogPost.title, url: seoData.canonical }
        );
        break;
      case 'category':
        breadcrumbs.push(
          { name: "Peptides", url: `${siteUrl}/peptides` },
          { name: category.title, url: seoData.canonical }
        );
        break;
      case 'research':
        breadcrumbs.push(
          { name: "Research", url: `${siteUrl}/research` },
          { name: category.title, url: seoData.canonical }
        );
        break;
    }

    return {
      "@context": "https://schema.org",
      "@type": "BreadcrumbList",
      "itemListElement": breadcrumbs.map((item, index) => ({
        "@type": "ListItem",
        "position": index + 1,
        "name": item.name,
        "item": item.url
      }))
    };
  };

  return (
    <Helmet>
      {/* Basic Meta Tags */}
      <title>{seoData.title}</title>
      <meta name="description" content={seoData.description} />
      <meta name="keywords" content={seoData.keywords} />
      <link rel="canonical" href={seoData.canonical} />

      {/* Robots & Indexing */}
      <meta name="robots" content={noIndex ? "noindex, nofollow" : "index, follow, max-snippet:-1, max-image-preview:large, max-video-preview:-1"} />
      <meta name="googlebot" content={noIndex ? "noindex, nofollow" : "index, follow, max-snippet:-1, max-image-preview:large, max-video-preview:-1"} />

      {/* Open Graph Meta Tags */}
      <meta property="og:title" content={seoData.title} />
      <meta property="og:description" content={seoData.description} />
      <meta property="og:image" content={seoData.image} />
      <meta property="og:image:width" content="1200" />
      <meta property="og:image:height" content="630" />
      <meta property="og:image:alt" content={`${seoData.title} - Professor Peptides`} />
      <meta property="og:url" content={seoData.canonical} />
      <meta property="og:type" content={seoData.type} />
      <meta property="og:site_name" content={siteName} />
      <meta property="og:locale" content="en_US" />

      {/* Twitter Card Meta Tags */}
      <meta name="twitter:card" content="summary_large_image" />
      <meta name="twitter:title" content={seoData.title} />
      <meta name="twitter:description" content={seoData.description} />
      <meta name="twitter:image" content={seoData.image} />
      <meta name="twitter:image:alt" content={`${seoData.title} - Professor Peptides`} />
      <meta name="twitter:site" content={twitterHandle} />
      <meta name="twitter:creator" content={twitterHandle} />

      {/* Additional SEO Meta Tags */}
      <meta name="author" content={author} />
      <meta name="publisher" content={siteName} />
      <meta name="copyright" content={siteName} />
      <meta name="theme-color" content="#2563eb" />
      <meta name="msapplication-TileColor" content="#2563eb" />
      <meta name="application-name" content={siteName} />

      {/* Article-specific tags */}
      {(pageType === 'blog' || pageType === 'peptide') && (
        <>
          <meta property="article:author" content={author} />
          <meta property="article:published_time" content={publishedDate || "2025-08-01"} />
          <meta property="article:modified_time" content={modifiedDate} />
          <meta property="article:section" content={pageType === 'blog' ? (blogPost?.category || 'Research') : 'Peptide Research'} />
        </>
      )}

      {/* Medical/Health Related Tags */}
      {(pageType === 'peptide' || pageType === 'calculator') && (
        <>
          <meta name="health-content-type" content="research-information" />
          <meta name="medical-disclaimer" content="For research purposes only - not for human consumption" />
          <meta name="target-audience" content="researchers, healthcare professionals, scientists" />
        </>
      )}

      {/* Language and Geographic Tags */}
      <meta name="language" content="English" />
      <meta name="geo.region" content="US" />
      <meta name="geo.placename" content="United States" />

      {/* Structured Data - Main Schema */}
      <script type="application/ld+json">
        {JSON.stringify(generateStructuredData(), null, 2)}
      </script>

      {/* Structured Data - Breadcrumbs */}
      <script type="application/ld+json">
        {JSON.stringify(generateBreadcrumbs(), null, 2)}
      </script>

      {/* Additional Schema for FAQ pages */}
      {pageType === 'peptide' && peptide && (
        <script type="application/ld+json">
          {JSON.stringify({
            "@context": "https://schema.org",
            "@type": "FAQPage",
            "mainEntity": [
              {
                "@type": "Question",
                "name": `What is ${peptide.name}?`,
                "acceptedAnswer": {
                  "@type": "Answer",
                  "text": seoData.description
                }
              },
              {
                "@type": "Question",
                "name": `Is ${peptide.name} FDA approved?`,
                "acceptedAnswer": {
                  "@type": "Answer",
                  "text": peptide.fdaApproved 
                    ? `Yes, ${peptide.name} is FDA approved for specific medical uses: ${peptide.approvedFor?.join(', ')}.`
                    : `No, ${peptide.name} is currently for research use only and not approved by the FDA for human consumption.`
                }
              },
              {
                "@type": "Question",
                "name": `Is ${peptide.name} banned by WADA?`,
                "acceptedAnswer": {
                  "@type": "Answer",
                  "text": peptide.wadaBanned 
                    ? `Yes, ${peptide.name} is prohibited by WADA for competitive athletes.`
                    : `No, ${peptide.name} is not currently banned by WADA for competitive sports.`
                }
              }
            ]
          }, null, 2)}
        </script>
      )}
    </Helmet>
  );
};

export default MetaTagsEnhanced;

