import React from 'react';

/**
 * Structured Data Component
 * Generates Schema.org structured data for different content types
 */
const StructuredData = ({ type, data }) => {
  let structuredData = {};

  switch (type) {
    case 'website':
      structuredData = {
        "@context": "https://schema.org",
        "@type": "WebSite",
        "@id": "https://www.professorpeptides.org/#website",
        "url": "https://www.professorpeptides.org/",
        "name": "Peptide Professor",
        "description": "Leading resource for research peptides, calculators, and scientific insights",
        "potentialAction": {
          "@type": "SearchAction",
          "target": {
            "@type": "EntryPoint",
            "urlTemplate": "https://www.professorpeptides.org/search?q={search_term_string}"
          },
          "query-input": "required name=search_term_string"
        },
        "sameAs": [
          "https://twitter.com/peptideprofessor",
          "https://linkedin.com/company/peptideprofessor"
        ]
      };
      break;

    case 'organization':
      structuredData = {
        "@context": "https://schema.org",
        "@type": "Organization",
        "@id": "https://www.professorpeptides.org/#organization",
        "name": "Peptide Professor",
        "url": "https://www.professorpeptides.org/",
        "logo": {
          "@type": "ImageObject",
          "url": "https://www.professorpeptides.org/logo.png",
          "width": 400,
          "height": 400
        },
        "description": "Professional platform for peptide research, education, and calculation tools",
        "contactPoint": {
          "@type": "ContactPoint",
          "contactType": "customer service",
          "email": "info@peptideprofessor.com",
          "availableLanguage": "English"
        },
        "sameAs": [
          "https://twitter.com/peptideprofessor",
          "https://linkedin.com/company/peptideprofessor"
        ]
      };
      break;

    case 'faq':
      structuredData = {
        "@context": "https://schema.org",
        "@type": "FAQPage",
        "mainEntity": (data.questions || []).map(qa => ({
          "@type": "Question",
          "name": qa.question,
          "acceptedAnswer": {
            "@type": "Answer",
            "text": qa.answer
          }
        }))
      };
      break;

    case 'calculator':
      structuredData = {
        "@context": "https://schema.org",
        "@type": "SoftwareApplication",
        "name": data.name || "Peptide Calculator",
        "description": data.description || "Professional peptide calculation tool",
        "applicationCategory": "UtilityApplication",
        "operatingSystem": "Web Browser",
        "offers": {
          "@type": "Offer",
          "price": "0",
          "priceCurrency": "USD"
        },
        "aggregateRating": {
          "@type": "AggregateRating",
          "ratingValue": data.rating || "4.8",
          "reviewCount": data.reviewCount || "1200",
          "bestRating": "5",
          "worstRating": "1"
        }
      };
      break;

    case 'howto':
      structuredData = {
        "@context": "https://schema.org",
        "@type": "HowTo",
        "name": data.name,
        "description": data.description,
        "image": data.image,
        "estimatedCost": {
          "@type": "MonetaryAmount",
          "currency": "USD",
          "value": "0"
        },
        "supply": (data.supplies || []).map(supply => ({
          "@type": "HowToSupply",
          "name": supply
        })),
        "tool": (data.tools || []).map(tool => ({
          "@type": "HowToTool", 
          "name": tool
        })),
        "step": (data.steps || []).map((step, index) => ({
          "@type": "HowToStep",
          "position": index + 1,
          "name": step.name,
          "text": step.text,
          "image": step.image
        }))
      };
      break;

    case 'medicalcondition':
      structuredData = {
        "@context": "https://schema.org",
        "@type": "MedicalCondition",
        "name": data.name,
        "description": data.description,
        "code": {
          "@type": "MedicalCode",
          "codeValue": data.icdCode,
          "codingSystem": "ICD-10"
        },
        "possibleTreatment": (data.treatments || []).map(treatment => ({
          "@type": "MedicalTherapy",
          "name": treatment
        }))
      };
      break;

    case 'drug':
      structuredData = {
        "@context": "https://schema.org",
        "@type": "Drug",
        "name": data.name,
        "description": data.description,
        "activeIngredient": data.activeIngredient,
        "dosageForm": data.dosageForm,
        "drugClass": data.drugClass,
        "administrationRoute": data.route,
        "clinicalPharmacology": data.pharmacology,
        "warning": (data.warnings || []).map(warning => ({
          "@type": "DrugWarning",
          "text": warning
        }))
      };
      break;

    default:
      return null;
  }

  return (
    <script
      type="application/ld+json"
      dangerouslySetInnerHTML={{ __html: JSON.stringify(structuredData, null, 2) }}
    />
  );
};

export default StructuredData;

import React from 'react';

/**
 * Enhanced Structured Data Component for Professional SEO
 * Generates comprehensive Schema.org structured data for all content types
 * in the Professor Peptides website
 */
const StructuredDataEnhanced = ({ type, data = {}, pageUrl, siteName = 'Professor Peptides' }) => {
  const siteUrl = 'https://www.professorpeptides.org';

  const generateStructuredData = () => {
    switch (type) {
      case 'medical-website':
        return generateMedicalWebsiteSchema();
      case 'medical-organization':
        return generateMedicalOrganizationSchema();
      case 'research-peptide':
        return generateResearchPeptideSchema(data);
      case 'medical-calculator':
        return generateMedicalCalculatorSchema(data);
      case 'research-article':
        return generateResearchArticleSchema(data);
      case 'blog-post':
        return generateBlogPostSchema(data);
      case 'faq-page':
        return generateFAQPageSchema(data);
      case 'how-to-guide':
        return generateHowToSchema(data);
      case 'medical-condition':
        return generateMedicalConditionSchema(data);
      case 'drug-information':
        return generateDrugSchema(data);
      case 'course-education':
        return generateCourseSchema(data);
      case 'video-content':
        return generateVideoSchema(data);
      case 'local-business':
        return generateLocalBusinessSchema(data);
      case 'professional-service':
        return generateProfessionalServiceSchema(data);
      case 'dataset':
        return generateDatasetSchema(data);
      default:
        return null;
    }
  };

  const generateMedicalWebsiteSchema = () => ({
    "@context": "https://schema.org",
    "@type": "MedicalWebPage",
    "@id": `${siteUrl}/#medical-website`,
    "name": "Professor Peptides - Medical Research Platform",
    "description": "Comprehensive peptide research platform with medical oversight and professional-grade tools",
    "url": siteUrl,
    "medicalAudience": [
      {
        "@type": "MedicalAudienceType",
        "audienceType": "Researcher"
      },
      {
        "@type": "MedicalAudienceType", 
        "audienceType": "HealthcareProfessional"
      },
      {
        "@type": "MedicalAudienceType",
        "audienceType": "Scientist"
      }
    ],
    "about": [
      {
        "@type": "MedicalCondition",
        "name": "Metabolic Health Disorders"
      },
      {
        "@type": "MedicalTherapy",
        "name": "Peptide Therapy"
      }
    ],
    "dateModified": new Date().toISOString(),
    "lastReviewed": new Date().toISOString(),
    "reviewedBy": {
      "@type": "Person",
      "name": "Dr. Sheraz Ahmad",
      "jobTitle": "Medical Advisor",
      "worksFor": {
        "@type": "Organization",
        "name": "Professor Peptides"
      }
    },
    "publisher": {
      "@type": "Organization",
      "name": siteName,
      "url": siteUrl
    }
  });

  const generateMedicalOrganizationSchema = () => ({
    "@context": "https://schema.org",
    "@type": "MedicalOrganization",
    "@id": `${siteUrl}/#organization`,
    "name": siteName,
    "alternateName": ["Professor Peptides", "Peptide Professor"],
    "url": siteUrl,
    "logo": {
      "@type": "ImageObject",
      "url": `${siteUrl}/images/logo-structured-data.png`,
      "width": 400,
      "height": 400
    },
    "description": "Medical research organization specializing in peptide therapy research and education with board-certified physician oversight",
    "foundingDate": "2025",
    "medicalSpecialty": [
      "Endocrinology",
      "Hormone Therapy", 
      "Peptide Therapy",
      "Metabolic Medicine",
      "Regenerative Medicine"
    ],
    "knowsAbout": [
      "GLP-1 Receptor Agonists",
      "Research Peptides",
      "Hormone Optimization",
      "Metabolic Health",
      "Tissue Repair Peptides",
      "Clinical Research"
    ],
    "employee": [
      {
        "@type": "Person",
        "name": "Shawn Younai",
        "jobTitle": "Founder & CEO",
        "worksFor": {
          "@type": "Organization",
          "name": siteName
        }
      },
      {
        "@type": "Person",
        "name": "Dr. Sheraz Ahmad",
        "jobTitle": "Medical Advisor",
        "hasCredential": [
          {
            "@type": "EducationalOccupationalCredential",
            "credentialCategory": "Medical Degree",
            "educationalLevel": "Doctor of Medicine"
          },
          {
            "@type": "EducationalOccupationalCredential", 
            "credentialCategory": "Board Certification",
            "educationalLevel": "Family Medicine"
          }
        ],
        "worksFor": {
          "@type": "Organization",
          "name": siteName
        }
      }
    ],
    "contactPoint": {
      "@type": "ContactPoint",
      "contactType": "Research Inquiries",
      "email": "research@professorpeptides.org",
      "availableLanguage": ["English"]
    },
    "sameAs": [
      "https://twitter.com/professorpeptides",
      "https://linkedin.com/company/professorpeptides"
    ]
  });

  const generateResearchPeptideSchema = (peptide) => ({
    "@context": "https://schema.org",
    "@type": ["Drug", "BioChemEntity"],
    "@id": `${pageUrl}#peptide`,
    "name": peptide.name,
    "alternateName": peptide.aliases || [],
    "identifier": peptide.slug,
    "description": peptide.description,
    "url": pageUrl,
    "drugClass": peptide.category || "Research Peptide",
    "activeIngredient": peptide.name,
    "chemicalStructure": peptide.chemicalMakeup,
    "administrationRoute": peptide.routes?.join(", ") || "Various",
    "mechanismOfAction": peptide.mechanism,
    "clinicalPharmacology": peptide.researchFindings?.join(". "),
    "warning": peptide.sideEffects || [],
    "isAvailableGenerically": false,
    "isPrescriptionOnly": peptide.fdaApproved || false,
    "legalStatus": peptide.fdaApproved ? "Prescription" : "Research Only",
    "additionalProperty": [
      {
        "@type": "PropertyValue",
        "name": "FDA Status",
        "value": peptide.fdaApproved ? "FDA Approved" : "Research Only"
      },
      {
        "@type": "PropertyValue",
        "name": "WADA Status", 
        "value": peptide.wadaBanned ? "WADA Banned" : "WADA Permitted"
      },
      {
        "@type": "PropertyValue",
        "name": "Research Category",
        "value": peptide.category
      }
    ],
    "potentialUse": peptide.benefits || [],
    "administrationRoute": peptide.routes || ["Subcutaneous", "Intramuscular"],
    "dosageForm": "Injectable Solution",
    "contraindication": peptide.contraindications || [],
    "adverseOutcome": peptide.sideEffects || [],
    "study": peptide.studyReferences?.map(ref => ({
      "@type": "MedicalStudy",
      "name": ref.title,
      "url": ref.url,
      "studyType": ref.type || "Clinical Study"
    })) || []
  });

  const generateMedicalCalculatorSchema = (calculator) => ({
    "@context": "https://schema.org",
    "@type": "SoftwareApplication",
    "@id": `${pageUrl}#calculator`,
    "name": calculator.name,
    "description": calculator.description,
    "applicationCategory": "HealthApplication",
    "applicationSubCategory": "Medical Calculator",
    "operatingSystem": "Web Browser",
    "browserRequirements": "HTML5, JavaScript enabled",
    "permissions": "No special permissions required",
    "softwareVersion": "2.0",
    "releaseNotes": "Enhanced accuracy and mobile optimization",
    "offers": {
      "@type": "Offer",
      "price": "0",
      "priceCurrency": "USD",
      "availability": "https://schema.org/InStock",
      "eligibleRegion": "Worldwide"
    },
    "aggregateRating": {
      "@type": "AggregateRating", 
      "ratingValue": calculator.rating || "4.8",
      "reviewCount": calculator.reviewCount || "1250",
      "bestRating": "5",
      "worstRating": "1"
    },
    "featureList": [
      "Medical-grade accuracy",
      "Instant calculations", 
      "Professional results",
      "Mobile responsive design",
      "Evidence-based formulas",
      "Real-time validation"
    ],
    "screenshot": `${siteUrl}/images/calculators/${calculator.slug}-screenshot.jpg`,
    "downloadUrl": pageUrl,
    "installUrl": pageUrl,
    "memoryRequirements": "Minimal",
    "processorRequirements": "Any modern processor",
    "storageRequirements": "No local storage required",
    "interactionCount": calculator.usageCount || "50000+",
    "dateCreated": calculator.dateCreated || "2025-01-01",
    "dateModified": new Date().toISOString(),
    "creator": {
      "@type": "Organization",
      "name": siteName
    },
    "maintainer": {
      "@type": "Organization",
      "name": siteName
    }
  });

  const generateResearchArticleSchema = (article) => ({
    "@context": "https://schema.org",
    "@type": "ScholarlyArticle",
    "@id": `${pageUrl}#article`,
    "headline": article.title,
    "name": article.title,
    "description": article.description,
    "image": article.image,
    "url": pageUrl,
    "datePublished": article.publishedDate,
    "dateModified": article.modifiedDate || new Date().toISOString(),
    "author": {
      "@type": "Person",
      "name": article.author || "Professor Peptides Research Team",
      "affiliation": {
        "@type": "Organization",
        "name": siteName
      }
    },
    "publisher": {
      "@type": "Organization",
      "name": siteName,
      "logo": {
        "@type": "ImageObject",
        "url": `${siteUrl}/images/logo-structured-data.png`
      }
    },
    "about": article.topics?.map(topic => ({
      "@type": "Thing",
      "name": topic
    })) || [],
    "citation": article.citations?.map(cite => ({
      "@type": "ScholarlyArticle",
      "name": cite.title,
      "url": cite.url
    })) || [],
    "keywords": article.keywords?.join(", "),
    "articleSection": article.category || "Research",
    "wordCount": article.wordCount,
    "educationalLevel": "Graduate",
    "educationalUse": "Research",
    "learningResourceType": "Article",
    "audience": {
      "@type": "EducationalAudience",
      "educationalRole": ["Researcher", "Healthcare Professional", "Graduate Student"]
    },
    "mainEntity": {
      "@type": "Thing",
      "name": article.mainTopic
    },
    "isPartOf": {
      "@type": "Website",
      "name": siteName,
      "url": siteUrl
    }
  });

  const generateBlogPostSchema = (post) => ({
    "@context": "https://schema.org",
    "@type": "BlogPosting",
    "@id": `${pageUrl}#blogpost`,
    "headline": post.title,
    "description": post.excerpt,
    "image": post.image,
    "url": pageUrl,
    "datePublished": post.date,
    "dateModified": post.modifiedDate || new Date().toISOString(),
    "author": {
      "@type": "Person",
      "name": post.author,
      "url": post.authorUrl
    },
    "publisher": {
      "@type": "Organization",
      "name": siteName,
      "logo": {
        "@type": "ImageObject",
        "url": `${siteUrl}/images/logo-structured-data.png`
      }
    },
    "mainEntityOfPage": {
      "@type": "WebPage",
      "@id": pageUrl
    },
    "articleSection": post.category,
    "keywords": post.keywords?.join(", "),
    "wordCount": post.wordCount,
    "timeRequired": post.reading_time,
    "inLanguage": "en-US",
    "isPartOf": {
      "@type": "Blog",
      "name": `${siteName} Blog`,
      "url": `${siteUrl}/blog`
    },
    "about": post.topics?.map(topic => ({
      "@type": "Thing",
      "name": topic
    })) || []
  });

  const generateFAQPageSchema = (faq) => ({
    "@context": "https://schema.org",
    "@type": "FAQPage",
    "@id": `${pageUrl}#faq`,
    "name": faq.title || "Frequently Asked Questions",
    "description": faq.description,
    "url": pageUrl,
    "mainEntity": (faq.questions || []).map(qa => ({
      "@type": "Question",
      "name": qa.question,
      "acceptedAnswer": {
        "@type": "Answer",
        "text": qa.answer,
        "author": {
          "@type": "Organization",
          "name": siteName
        },
        "dateCreated": qa.dateCreated || new Date().toISOString(),
        "upvoteCount": qa.upvotes || 0
      }
    })),
    "about": {
      "@type": "Thing",
      "name": faq.topic || "Peptide Research"
    },
    "audience": {
      "@type": "Audience",
      "audienceType": ["Researcher", "Healthcare Professional", "Patient"]
    }
  });

  const generateHowToSchema = (howTo) => ({
    "@context": "https://schema.org",
    "@type": "HowTo",
    "@id": `${pageUrl}#howto`,
    "name": howTo.name,
    "description": howTo.description,
    "image": howTo.image,
    "url": pageUrl,
    "datePublished": howTo.publishedDate,
    "totalTime": howTo.totalTime,
    "estimatedCost": {
      "@type": "MonetaryAmount",
      "currency": "USD",
      "value": howTo.cost || "0"
    },
    "supply": (howTo.supplies || []).map(supply => ({
      "@type": "HowToSupply",
      "name": supply.name,
      "image": supply.image,
      "requiredQuantity": supply.quantity
    })),
    "tool": (howTo.tools || []).map(tool => ({
      "@type": "HowToTool",
      "name": tool.name,
      "image": tool.image
    })),
    "step": (howTo.steps || []).map((step, index) => ({
      "@type": "HowToStep",
      "position": index + 1,
      "name": step.name,
      "text": step.text,
      "image": step.image,
      "url": step.url
    })),
    "video": howTo.video ? {
      "@type": "VideoObject",
      "name": howTo.video.title,
      "description": howTo.video.description,
      "thumbnailUrl": howTo.video.thumbnail,
      "contentUrl": howTo.video.url,
      "duration": howTo.video.duration
    } : undefined,
    "author": {
      "@type": "Organization",
      "name": siteName
    },
    "publisher": {
      "@type": "Organization", 
      "name": siteName
    }
  });

  const generateMedicalConditionSchema = (condition) => ({
    "@context": "https://schema.org",
    "@type": "MedicalCondition",
    "@id": `${pageUrl}#condition`,
    "name": condition.name,
    "alternateName": condition.alternateNames || [],
    "description": condition.description,
    "code": condition.icdCode ? {
      "@type": "MedicalCode",
      "codeValue": condition.icdCode,
      "codingSystem": "ICD-10"
    } : undefined,
    "associatedAnatomy": condition.anatomy ? {
      "@type": "AnatomicalStructure",
      "name": condition.anatomy
    } : undefined,
    "cause": condition.causes?.map(cause => ({
      "@type": "MedicalCause",
      "name": cause
    })) || [],
    "riskFactor": condition.riskFactors?.map(factor => ({
      "@type": "MedicalRiskFactor",
      "name": factor
    })) || [],
    "possibleTreatment": condition.treatments?.map(treatment => ({
      "@type": "MedicalTherapy",
      "name": treatment.name,
      "description": treatment.description
    })) || [],
    "possibleComplication": condition.complications?.map(comp => ({
      "@type": "MedicalCondition",
      "name": comp
    })) || [],
    "typicalTest": condition.diagnosticTests?.map(test => ({
      "@type": "MedicalTest",
      "name": test
    })) || []
  });

  const generateDrugSchema = (drug) => ({
    "@context": "https://schema.org",
    "@type": "Drug",
    "@id": `${pageUrl}#drug`,
    "name": drug.name,
    "description": drug.description,
    "activeIngredient": drug.activeIngredient,
    "dosageForm": drug.dosageForm,
    "drugClass": drug.drugClass,
    "administrationRoute": drug.administrationRoute,
    "availableStrength": drug.strengths?.map(strength => ({
      "@type": "DrugStrength",
      "name": strength
    })) || [],
    "clinicalPharmacology": drug.pharmacology,
    "mechanism": drug.mechanismOfAction,
    "indication": drug.indications?.map(indication => ({
      "@type": "MedicalIndication",
      "name": indication
    })) || [],
    "contraindication": drug.contraindications?.map(contra => ({
      "@type": "MedicalContraindication", 
      "name": contra
    })) || [],
    "warning": drug.warnings?.map(warning => ({
      "@type": "DrugWarning",
      "text": warning
    })) || [],
    "adverseOutcome": drug.adverseEffects?.map(effect => ({
      "@type": "MedicalSignOrSymptom",
      "name": effect
    })) || [],
    "drugInteraction": drug.interactions?.map(interaction => ({
      "@type": "DrugInteraction",
      "interactingDrug": {
        "@type": "Drug",
        "name": interaction.drug
      },
      "description": interaction.description
    })) || []
  });

  const generateCourseSchema = (course) => ({
    "@context": "https://schema.org",
    "@type": "Course",
    "@id": `${pageUrl}#course`,
    "name": course.name,
    "description": course.description,
    "provider": {
      "@type": "Organization",
      "name": siteName
    },
    "educationalLevel": course.level || "Intermediate",
    "about": course.topics?.map(topic => ({
      "@type": "Thing",
      "name": topic
    })) || [],
    "timeRequired": course.duration,
    "numberOfCredits": course.credits,
    "coursePrerequisites": course.prerequisites,
    "educationalCredentialAwarded": course.certification,
    "hasCourseInstance": {
      "@type": "CourseInstance",
      "courseMode": "Online",
      "instructor": {
        "@type": "Person", 
        "name": course.instructor
      }
    }
  });

  const generateVideoSchema = (video) => ({
    "@context": "https://schema.org",
    "@type": "VideoObject",
    "@id": `${pageUrl}#video`,
    "name": video.title,
    "description": video.description,
    "thumbnailUrl": video.thumbnail,
    "contentUrl": video.url,
    "embedUrl": video.embedUrl,
    "duration": video.duration,
    "uploadDate": video.uploadDate,
    "interactionStatistic": {
      "@type": "InteractionCounter",
      "interactionType": "https://schema.org/WatchAction",
      "userInteractionCount": video.viewCount
    },
    "publisher": {
      "@type": "Organization",
      "name": siteName
    }
  });

  const generateDatasetSchema = (dataset) => ({
    "@context": "https://schema.org",
    "@type": "Dataset",
    "@id": `${pageUrl}#dataset`,
    "name": dataset.name,
    "description": dataset.description,
    "url": pageUrl,
    "keywords": dataset.keywords?.join(", "),
    "creator": {
      "@type": "Organization",
      "name": siteName
    },
    "dateCreated": dataset.dateCreated,
    "dateModified": dataset.dateModified,
    "distribution": {
      "@type": "DataDownload",
      "encodingFormat": dataset.format || "JSON",
      "contentUrl": dataset.downloadUrl
    },
    "includedInDataCatalog": {
      "@type": "DataCatalog",
      "name": `${siteName} Research Database`
    },
    "measurementTechnique": dataset.techniques || [],
    "variableMeasured": dataset.variables || []
  });

  const structuredData = generateStructuredData();

  if (!structuredData) return null;

  return (
    <script
      type="application/ld+json"
      dangerouslySetInnerHTML={{ __html: JSON.stringify(structuredData, null, 2) }}
    />
  );
};

export default StructuredDataEnhanced;

