import React from 'react';
import { Button } from '../ui/button';
import { Share2, Mail, MessageCircle, Facebook, Twitter } from 'lucide-react';

const SocialShareButtons = ({ peptide, url, title, description, image }) => {
  const shareUrl = url || window.location.href;
  const shareTitle = title || `${peptide.name} - Research Peptide Information`;
  const shareDescription = description || peptide.description?.substring(0, 160) || 'Professional research peptide information and protocols';
  const shareImage = image || 'https://www.professorpeptides.org/images/peptide-og-default.jpg';

  const handleShare = async (platform) => {
    // Analytics event
    if (window.gtag) {
      window.gtag('event', 'social_share', {
        peptide: peptide.slug,
        platform: platform,
        page_title: shareTitle,
        custom_parameters: {
          peptide_handle: peptide.slug,
          share_platform: platform
        }
      });
    }

    let shareUrlEncoded = encodeURIComponent(shareUrl);
    let titleEncoded = encodeURIComponent(shareTitle);
    let descriptionEncoded = encodeURIComponent(shareDescription);

    switch (platform) {
      case 'messages':
        if (navigator.share) {
          try {
            await navigator.share({
              title: shareTitle,
              text: shareDescription,
              url: shareUrl,
            });
          } catch (error) {
            // Fallback to SMS
            window.open(`sms:?body=${titleEncoded} - ${shareUrlEncoded}`);
          }
        } else {
          // Fallback to SMS
          window.open(`sms:?body=${titleEncoded} - ${shareUrlEncoded}`);
        }
        break;

      case 'mail':
        const mailSubject = encodeURIComponent(`Research Peptide: ${peptide.name}`);
        const mailBody = encodeURIComponent(`${shareDescription}\n\nLearn more: ${shareUrl}`);
        window.open(`mailto:?subject=${mailSubject}&body=${mailBody}`);
        break;

      case 'twitter':
        const twitterText = encodeURIComponent(`${shareTitle}\n\n${shareDescription}`);
        window.open(`https://twitter.com/intent/tweet?text=${twitterText}&url=${shareUrlEncoded}&hashtags=peptides,research`);
        break;

      case 'facebook':
        window.open(`https://www.facebook.com/sharer/sharer.php?u=${shareUrlEncoded}&quote=${titleEncoded}`);
        break;

      default:
        break;
    }
  };

  return (
    <div className="flex flex-col space-y-3">
      <h4 className="text-sm font-medium text-gray-900 flex items-center">
        <Share2 className="h-4 w-4 mr-2" />
        Share Information
      </h4>
      <div className="grid grid-cols-2 gap-2">
        <Button
          variant="outline"
          size="sm"
          onClick={() => handleShare('messages')}
          className="flex items-center justify-center"
          aria-label={`Share ${peptide.name} via Messages`}
        >
          <MessageCircle className="h-4 w-4 mr-2" />
          Messages
        </Button>
        <Button
          variant="outline"
          size="sm"
          onClick={() => handleShare('mail')}
          className="flex items-center justify-center"
          aria-label={`Share ${peptide.name} via Email`}
        >
          <Mail className="h-4 w-4 mr-2" />
          Mail
        </Button>
        <Button
          variant="outline"
          size="sm"
          onClick={() => handleShare('twitter')}
          className="flex items-center justify-center"
          aria-label={`Share ${peptide.name} on X (Twitter)`}
        >
          <Twitter className="h-4 w-4 mr-2" />
          X
        </Button>
        <Button
          variant="outline"
          size="sm"
          onClick={() => handleShare('facebook')}
          className="flex items-center justify-center"
          aria-label={`Share ${peptide.name} on Facebook`}
        >
          <Facebook className="h-4 w-4 mr-2" />
          Facebook
        </Button>
      </div>
      <p className="text-xs text-gray-500 text-center">
        Share research information responsibly
      </p>
    </div>
  );
};

export default SocialShareButtons;

