        <div className="flex-1">
          <h3 className="text-lg font-semibold text-gray-900">{author}</h3>
          <p className="text-blue-600 font-medium mb-1">{credentials}</p>
          <p className="text-sm text-gray-700 mb-2">{bio}</p>
          {institution && (
            <p className="text-sm text-gray-600">
              <span className="font-medium">Institution:</span> {institution}
            </p>
          )}
        </div>
