      {/* Content Metadata */}
      <div className="border-t border-gray-200 pt-4">
        <div className="flex items-center text-sm text-gray-600 mb-2">
          <Calendar className="h-4 w-4 mr-2" />
          <span>Last updated: {formatDate(lastUpdated)}</span>
        </div>

        {/* Medical Review Note */}
        <div className="bg-green-50 border border-green-200 rounded p-3 mb-3">
          <p className="text-xs text-green-800">
            <span className="font-semibold">âœ“ Medically Reviewed:</span> This content has been reviewed by our medical advisory board 
            and cross-referenced with peer-reviewed literature to ensure accuracy and clinical relevance.
          </p>
        </div>
      </div>

      {/* Citations */}
      {citations && citations.length > 0 && (
        <div className="border-t border-gray-200 pt-4">
          <h4 className="text-sm font-semibold text-gray-900 mb-2">References & Citations:</h4>
          <ul className="space-y-2">
            {citations.map((citation, index) => (
              <li key={index} className="text-xs text-gray-600 flex items-start">
                <span className="font-medium mr-2 text-gray-800">[{index + 1}]</span>
                <div className="flex-1">
                  <span>{citation.title}</span>
                  {citation.journal && (
                    <span className="italic"> {citation.journal}</span>
                  )}
                  {citation.year && (
                    <span> ({citation.year})</span>
                  )}
                  {citation.url && (
                    <a 
                      href={citation.url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-blue-600 hover:text-blue-800 ml-2 inline-flex items-center"
                    >
                      <ExternalLink className="h-3 w-3" />
                    </a>
                  )}
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default AuthorCredentials;

