import React, { useState, useEffect, useMemo } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import MetaTags from '../components/seo/MetaTags';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { Button } from '../components/ui/button';
import { 
  MapPin, 
  Phone, 
  Mail, 
  Globe, 
  Star, 
  Filter, 
  Search, 
  Clock,
  Shield,
  Award,
  Users,
  DollarSign,
  ChevronDown,
  ChevronUp,
  ExternalLink,
  CheckCircle,
  Info
} from 'lucide-react';
import { medSpas, medSpaServices, medSpaLocations, defaultLocation } from '../mock/medSpaData';

const MedSpaFinderPage = () => {
  const [searchLocation, setSearchLocation] = useState('');
  const [selectedServices, setSelectedServices] = useState([]);
  const [priceRange, setPriceRange] = useState('all');
  const [sortBy, setSortBy] = useState('rating');
  const [showFilters, setShowFilters] = useState(false);
  const [viewMode, setViewMode] = useState('grid');
  const [expandedFaq, setExpandedFaq] = useState(null);

  // Filter and sort med spas
  const filteredMedSpas = useMemo(() => {
    let filtered = [...medSpas];

    // Filter by location
    if (searchLocation.trim()) {
      filtered = filtered.filter(spa => 
        spa.city.toLowerCase().includes(searchLocation.toLowerCase()) ||
        spa.state.toLowerCase().includes(searchLocation.toLowerCase()) ||
        spa.name.toLowerCase().includes(searchLocation.toLowerCase())
      );
    }

    // Filter by services
    if (selectedServices.length > 0) {
      filtered = filtered.filter(spa =>
        selectedServices.some(service => spa.services.includes(service))
      );
    }

    // Filter by price range
    if (priceRange !== 'all') {
      filtered = filtered.filter(spa => spa.priceRange === priceRange);
    }

    // Sort results
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'rating':
          return b.rating - a.rating;
        case 'reviews':
          return b.reviewCount - a.reviewCount;
        case 'price-low':
          return a.consultationFee - b.consultationFee;
        case 'price-high':
          return b.consultationFee - a.consultationFee;
        case 'name':
          return a.name.localeCompare(b.name);
        default:
          return b.rating - a.rating;
      }
    });

    return filtered;
  }, [searchLocation, selectedServices, priceRange, sortBy]);

  const handleServiceToggle = (serviceId) => {
    setSelectedServices(prev => 
      prev.includes(serviceId) 
        ? prev.filter(id => id !== serviceId)
        : [...prev, serviceId]
    );
  };

  const clearFilters = () => {
    setSearchLocation('');
    setSelectedServices([]);
    setPriceRange('all');
    setSortBy('rating');
  };

  const getServiceName = (serviceId) => {
    const service = medSpaServices.find(s => s.id === serviceId);
    return service ? service.name : serviceId;
  };

  const renderStars = (rating) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star 
        key={i} 
        className={`h-4 w-4 ${i < Math.floor(rating) ? 'text-yellow-400 fill-current' : 'text-gray-300'}`} 
      />
    ));
